export default {
  name: 'VueCron',
  props: ['data'],
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      size: 'large',
      weekDays: ['天', '一', '二', '三', '四', '五', '六'].map(function (val) {
        return '星期' + val;
      }),
      result: {
        second: {},
        minute: {},
        hour: {},
        day: {},
        week: {},
        month: {},
        year: {}
      },
      defaultValue: {
        second: {
          cronEvery: '',
          incrementStart: 3,
          incrementIncrement: 5,
          rangeStart: 1,
          rangeEnd: 0,
          specificSpecific: []
        },
        minute: {
          cronEvery: '',
          incrementStart: 3,
          incrementIncrement: 5,
          rangeStart: 1,
          rangeEnd: '0',
          specificSpecific: []
        },
        hour: {
          cronEvery: '',
          incrementStart: 3,
          incrementIncrement: 5,
          rangeStart: '0',
          rangeEnd: '0',
          specificSpecific: []
        },
        day: {
          cronEvery: '',
          incrementStart: 1,
          incrementIncrement: '1',
          rangeStart: '',
          rangeEnd: '',
          specificSpecific: [],
          cronLastSpecificDomDay: 1,
          cronDaysBeforeEomMinus: 1,
          cronDaysNearestWeekday: 1
        },
        week: {
          cronEvery: '',
          incrementStart: 1,
          incrementIncrement: 1,
          specificSpecific: [],
          cronNthDayDay: 1,
          cronNthDayNth: 1
        },
        month: {
          cronEvery: '',
          incrementStart: 3,
          incrementIncrement: 5,
          rangeStart: 1,
          rangeEnd: 1,
          specificSpecific: []
        },
        year: {
          cronEvery: '',
          incrementStart: 2017,
          incrementIncrement: 1,
          rangeStart: 2019,
          rangeEnd: 2019,
          specificSpecific: []
        },
        label: ''
      }
    };
  },
  computed: {
    modalWidth: function modalWidth() {
      return 608;
    },
    secondsText: function secondsText() {
      var seconds = '';
      var cronEvery = this.result.second.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          seconds = '*';
          break;

        case '2':
          seconds = this.result.second.incrementStart + '/' + this.result.second.incrementIncrement;
          break;

        case '3':
          this.result.second.specificSpecific.map(function (val) {
            seconds += val + ',';
          });
          seconds = seconds.slice(0, -1);
          break;

        case '4':
          seconds = this.result.second.rangeStart + '-' + this.result.second.rangeEnd;
          break;
      }

      return seconds;
    },
    minutesText: function minutesText() {
      var minutes = '';
      var cronEvery = this.result.minute.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          minutes = '*';
          break;

        case '2':
          minutes = this.result.minute.incrementStart + '/' + this.result.minute.incrementIncrement;
          break;

        case '3':
          this.result.minute.specificSpecific.map(function (val) {
            minutes += val + ',';
          });
          minutes = minutes.slice(0, -1);
          break;

        case '4':
          minutes = this.result.minute.rangeStart + '-' + this.result.minute.rangeEnd;
          break;
      }

      return minutes;
    },
    hoursText: function hoursText() {
      var hours = '';
      var cronEvery = this.result.hour.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          hours = '*';
          break;

        case '2':
          hours = this.result.hour.incrementStart + '/' + this.result.hour.incrementIncrement;
          break;

        case '3':
          this.result.hour.specificSpecific.map(function (val) {
            hours += val + ',';
          });
          hours = hours.slice(0, -1);
          break;

        case '4':
          hours = this.result.hour.rangeStart + '-' + this.result.hour.rangeEnd;
          break;
      }

      return hours;
    },
    daysText: function daysText() {
      var days = '';
      var cronEvery = this.result.day.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          break;

        case '2':
        case '4':
        case '11':
          days = '?';
          break;

        case '3':
          days = this.result.day.incrementStart + '/' + this.result.day.incrementIncrement;
          break;

        case '5':
          this.result.day.specificSpecific.map(function (val) {
            days += val + ',';
          });
          days = days.slice(0, -1);
          break;

        case '6':
          days = "L";
          break;

        case '7':
          days = "LW";
          break;

        case '8':
          days = this.result.day.cronLastSpecificDomDay + 'L';
          break;

        case '9':
          days = 'L-' + this.result.day.cronDaysBeforeEomMinus;
          break;

        case '10':
          days = this.result.day.cronDaysNearestWeekday + "W";
          break;
      }

      return days;
    },
    weeksText: function weeksText() {
      var weeks = '';
      var cronEvery = this.result.day.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
        case '3':
        case '5':
          weeks = '?';
          break;

        case '2':
          weeks = this.result.week.incrementStart + '/' + this.result.week.incrementIncrement;
          break;

        case '4':
          this.result.week.specificSpecific.map(function (val) {
            weeks += val + ',';
          });
          weeks = weeks.slice(0, -1);
          break;

        case '6':
        case '7':
        case '8':
        case '9':
        case '10':
          weeks = "?";
          break;

        case '11':
          weeks = this.result.week.cronNthDayDay + "#" + this.result.week.cronNthDayNth;
          break;
      }

      return weeks;
    },
    monthsText: function monthsText() {
      var months = '';
      var cronEvery = this.result.month.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          months = '*';
          break;

        case '2':
          months = this.result.month.incrementStart + '/' + this.result.month.incrementIncrement;
          break;

        case '3':
          this.result.month.specificSpecific.map(function (val) {
            months += val + ',';
          });
          months = months.slice(0, -1);
          break;

        case '4':
          months = this.result.month.rangeStart + '-' + this.result.month.rangeEnd;
          break;
      }

      return months;
    },
    yearsText: function yearsText() {
      var years = '';
      var cronEvery = this.result.year.cronEvery || '';

      switch (cronEvery.toString()) {
        case '1':
          years = '*';
          break;

        case '2':
          years = this.result.year.incrementStart + '/' + this.result.year.incrementIncrement;
          break;

        case '3':
          this.result.year.specificSpecific.map(function (val) {
            years += val + ',';
          });
          years = years.slice(0, -1);
          break;

        case '4':
          years = this.result.year.rangeStart + '-' + this.result.year.rangeEnd;
          break;
      }

      return years;
    },
    cron: function cron() {
      return "".concat(this.secondsText || '*', " ").concat(this.minutesText || '*', " ").concat(this.hoursText || '*', " ").concat(this.daysText || '*', " ").concat(this.monthsText || '*', " ").concat(this.weeksText || '?', " ").concat(this.yearsText || '*');
    }
  },
  watch: {
    visible: {
      handler: function handler() {
        // if(this.data){
        //   //this. result = Object.keys(this.data.value).length>0?this.deepCopy(this.data.value):this.deepCopy(this.defaultValue);
        //   //this.result = Object.keys(this.data.value).length>0?clone(this.data.value):clone(this.defaultValue);
        //   //this.result = Object.keys(this.data.value).length>0?clone(JSON.parse(this.data.value)):clone(this.defaultValue);
        //   this.result = Object.keys(this.data.value).length>0?JSON.parse(this.data.value):JSON.parse(JSON.stringify(this.defaultValue));
        // }else{
        //   //this.result = this.deepCopy(this.defaultValue);
        //   //this.result = clone(this.defaultValue);
        //   this.result = JSON.parse(JSON.stringify(this.defaultValue));
        // }
        var label = this.data;

        if (label) {
          this.secondsReverseExp(label);
          this.minutesReverseExp(label);
          this.hoursReverseExp(label);
          this.daysReverseExp(label);
          this.daysReverseExp(label);
          this.monthsReverseExp(label);
          this.yearReverseExp(label);
          JSON.parse(JSON.stringify(label));
        } else {
          this.result = JSON.parse(JSON.stringify(this.defaultValue));
        }
      }
    }
  },
  methods: {
    show: function show() {
      this.visible = true; // console.log('secondsReverseExp',this.secondsReverseExp(this.data));
      // console.log('minutesReverseExp',this.minutesReverseExp(this.data));
      // console.log('hoursReverseExp',this.hoursReverseExp(this.data));
      // console.log('daysReverseExp',this.daysReverseExp(this.data));
      // console.log('monthsReverseExp',this.monthsReverseExp(this.data));
      // console.log('yearReverseExp',this.yearReverseExp(this.data));
    },
    handleSubmit: function handleSubmit() {
      this.$emit('ok', this.cron);
      this.close();
      this.visible = false;
    },
    close: function close() {
      this.visible = false;
    },
    secondsReverseExp: function secondsReverseExp(seconds) {
      var val = seconds.split(" ")[0]; //alert(val);

      var second = {
        cronEvery: '',
        incrementStart: 3,
        incrementIncrement: 5,
        rangeStart: 1,
        rangeEnd: 0,
        specificSpecific: []
      };

      switch (true) {
        case val.includes('*'):
          second.cronEvery = '1';
          break;

        case val.includes('/'):
          second.cronEvery = '2';
          second.incrementStart = val.split('/')[0];
          second.incrementIncrement = val.split('/')[1];
          break;

        case val.includes(','):
          second.cronEvery = '3';
          second.specificSpecific = val.split(',').map(Number).sort();
          break;

        case val.includes('-'):
          second.cronEvery = '4';
          second.rangeStart = val.split('-')[0];
          second.rangeEnd = val.split('-')[1];
          break;

        default:
          second.cronEvery = '1';
      }

      this.result.second = second;
    },
    minutesReverseExp: function minutesReverseExp(minutes) {
      var val = minutes.split(" ")[1];
      var minute = {
        cronEvery: '',
        incrementStart: 3,
        incrementIncrement: 5,
        rangeStart: 1,
        rangeEnd: 0,
        specificSpecific: []
      };

      switch (true) {
        case val.includes('*'):
          minute.cronEvery = '1';
          break;

        case val.includes('/'):
          minute.cronEvery = '2';
          minute.incrementStart = val.split('/')[0];
          minute.incrementIncrement = val.split('/')[1];
          break;

        case val.includes(','):
          minute.cronEvery = '3';
          minute.specificSpecific = val.split(',').map(Number).sort();
          break;

        case val.includes('-'):
          minute.cronEvery = '4';
          minute.rangeStart = val.split('-')[0];
          minute.rangeEnd = val.split('-')[1];
          break;

        default:
          minute.cronEvery = '1';
      }

      this.result.minute = minute;
    },
    hoursReverseExp: function hoursReverseExp(hours) {
      var val = hours.split(" ")[2];
      var hour = {
        cronEvery: '',
        incrementStart: 3,
        incrementIncrement: 5,
        rangeStart: 1,
        rangeEnd: '0',
        specificSpecific: []
      };

      switch (true) {
        case val.includes('*'):
          hour.cronEvery = '1';
          break;

        case val.includes('/'):
          hour.cronEvery = '2';
          hour.incrementStart = val.split('/')[0];
          hour.incrementIncrement = val.split('/')[1];
          break;

        case val.includes(','):
          hour.cronEvery = '3';
          hour.specificSpecific = val.split(',').map(Number).sort();
          break;

        case val.includes('-'):
          hour.cronEvery = '4';
          hour.rangeStart = val.split('-')[0];
          hour.rangeEnd = val.split('-')[1];
          break;

        default:
          hour.cronEvery = '1';
      }

      this.result.hour = hour;
    },
    daysReverseExp: function daysReverseExp(cron) {
      var days = cron.split(" ")[3];
      var weeks = cron.split(" ")[5];
      var day = {
        cronEvery: '',
        incrementStart: 1,
        incrementIncrement: 1,
        rangeStart: 1,
        rangeEnd: 1,
        specificSpecific: [],
        cronLastSpecificDomDay: 1,
        cronDaysBeforeEomMinus: 1,
        cronDaysNearestWeekday: 1
      };
      var week = {
        cronEvery: '',
        incrementStart: 1,
        incrementIncrement: 1,
        specificSpecific: [],
        cronNthDayDay: 1,
        cronNthDayNth: '1'
      };

      if (!days.includes('?')) {
        switch (true) {
          case days.includes('*'):
            day.cronEvery = '1';
            break;

          case days.includes('?'):
            // 2、4、11
            break;

          case days.includes('/'):
            day.cronEvery = '3';
            day.incrementStart = days.split('/')[0];
            day.incrementIncrement = days.split('/')[1];
            break;

          case days.includes(','):
            day.cronEvery = '5';
            day.specificSpecific = days.split(',').map(Number).sort(); // day.specificSpecific.forEach(function (value, index) {
            //   day.specificSpecific[index] = value -1;
            // });

            break;

          case days.includes('LW'):
            day.cronEvery = '7';
            break;

          case days.includes('L-'):
            day.cronEvery = '9';
            day.cronDaysBeforeEomMinus = days.split('L-')[1];
            break;

          case days.includes('L'):
            //alert(days);
            if (days.len == 1) {
              day.cronEvery = '6';
              day.cronLastSpecificDomDay = '1';
            } else {
              day.cronEvery = '8';
              day.cronLastSpecificDomDay = Number(days.split('L')[0]);
            }

            break;

          case days.includes('W'):
            day.cronEvery = '10';
            day.cronDaysNearestWeekday = days.split('W')[0];
            break;

          default:
            day.cronEvery = '1';
        }
      } else {
        switch (true) {
          case weeks.includes('/'):
            day.cronEvery = '2';
            week.incrementStart = weeks.split("/")[0];
            week.incrementIncrement = weeks.split("/")[1];
            break;

          case weeks.includes(','):
            day.cronEvery = '4';
            week.specificSpecific = weeks.split(',').map(Number).sort();
            break;

          case '#':
            day.cronEvery = '11';
            week.cronNthDayDay = weeks.split("#")[0];
            week.cronNthDayNth = weeks.split("#")[1];
            break;

          default:
            day.cronEvery = '1';
            week.cronEvery = '1';
        }
      }

      this.result.day = day;
      this.result.week = week;
    },
    monthsReverseExp: function monthsReverseExp(cron) {
      var months = cron.split(" ")[4];
      var month = {
        cronEvery: '',
        incrementStart: 3,
        incrementIncrement: 5,
        rangeStart: 1,
        rangeEnd: 1,
        specificSpecific: []
      };

      switch (true) {
        case months.includes('*'):
          month.cronEvery = '1';
          break;

        case months.includes('/'):
          month.cronEvery = '2';
          month.incrementStart = months.split('/')[0];
          month.incrementIncrement = months.split('/')[1];
          break;

        case months.includes(','):
          month.cronEvery = '3';
          month.specificSpecific = months.split(',').map(Number).sort();
          break;

        case months.includes('-'):
          month.cronEvery = '4';
          month.rangeStart = months.split('-')[0];
          month.rangeEnd = months.split('-')[1];
          break;

        default:
          month.cronEvery = '1';
      }

      this.result.month = month;
    },
    yearReverseExp: function yearReverseExp(cron) {
      var years = cron.split(" ")[6];
      var year = {
        cronEvery: '',
        incrementStart: 3,
        incrementIncrement: 5,
        rangeStart: 2019,
        rangeEnd: 2019,
        specificSpecific: []
      };

      switch (true) {
        case years.includes('*'):
          year.cronEvery = '1';
          break;

        case years.includes('/'):
          year.cronEvery = '2';
          year.incrementStart = years.split('/')[0];
          year.incrementIncrement = years.split('/')[1];
          break;

        case years.includes(','):
          year.cronEvery = '3';
          year.specificSpecific = years.split(',').map(Number).sort();
          break;

        case years.includes('-'):
          year.cronEvery = '4';
          year.rangeStart = years.split('-')[0];
          year.rangeEnd = years.split('-')[1];
          break;

        default:
          year.cronEvery = '1';
      }

      this.result.year = year;
    }
  }
};
