import JSelectDepart from '@/components/jeecgbiz/JSelectDepart';
import { getAction, postAction } from "@/api/manage";
import JSelectUserByDep from '@/components/jeecgbiz/JSelectUserByDep';
export default {
  name: 'releaseScope',
  components: {
    JSelectDepart: JSelectDepart,
    JSelectUserByDep: JSelectUserByDep
  },
  props: {
    backData: {
      type: Object,
      required: false
    },
    modalWidth: {
      type: Number,
      default: 1250,
      required: false
    },
    value: {
      type: String,
      required: false
    },
    disabled: {
      type: Boolean,
      required: false,
      default: false
    }
  },
  data: function data() {
    return {
      userIdLists: '',
      selectedDepUsers: "",
      iIsLimits: '',
      departId: '',
      userRealName: '',
      userIdList: "",
      url: {
        commitPer: '/oaBus/oaBusdata/commitPer',
        updateBusdata: '/oaBus/dynamic/updateData',
        //修改发布状态
        deletePremit: '/oaBus/dynamic/deletePremit' //修改权限表发布数据并修改发布范围字段

      }
    };
  },
  created: function created() {
    this.initData();
  },
  mounted: function mounted() {
    this.selectedDepUsers = this.value;
  },
  watch: {
    value: function value(val) {
      this.selectedDepUsers = val;
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  },
  methods: {
    initData: function initData() {
      var _this = this;

      setTimeout(function () {
        var backData1 = _this.backData;

        if (backData1.i_bigint3 != undefined) {
          _this.iIsLimits = backData1.i_bigint3;
        } else {
          _this.iIsLimits = 1;
        }

        if (backData1.i_bigint3 == 2) {
          _this.departId = backData1.s_varchar3;
        }

        if (backData1.i_bigint3 == 3) {
          _this.userRealName = backData1.s_varchar3;
          _this.userIdLists = backData1.s_varchar4.split(",");
        }
      }, 800);
    },
    userIds: function userIds(data) {
      // alert(data);
      this.userIdLists = data;
      this.$emit('userIDs', data);
    },
    //监听选择范围
    selectJG: function selectJG(e) {
      //全行
      if (e.target.value == 1) {
        var publish = {};
        publish.iIsLimits = e.target.value;
        this.$emit("change1", publish);
      }
    },
    senUserName: function senUserName(data) {
      this.userRealName = data.toString();
      this.$emit('userNames', data);
    },
    //获取选择范围数据
    change: function change(e) {
      // console.log(e);
      var publish = {};
      publish.iIsLimits = this.iIsLimits;

      if (this.iIsLimits == 2) {
        publish.departId = e;
      }

      if (this.iIsLimits == 3) {
        publish.userRealName = this.userRealName;
        publish.userIdList = this.userIdList;
      }

      this.$emit("change1", publish);
    },
    //通过组织机构筛选选择用户
    onSearchDepUser: function onSearchDepUser() {
      this.$refs.selectModal.showModal();
      this.onSearchDepUserCallBack('');
    },
    onSearchDepUserCallBack: function onSearchDepUserCallBack(selectedDepUsers) {
      this.selectedDepUsers = selectedDepUsers;
      this.$emit("change", selectedDepUsers);
    },
    selectFun: function selectFun(index) {
      //alert(typeof  index)
      if (index == 3) {
        this.departId = '';
        this.iIsLimits = '';
        this.$refs.selectModal.showModal();
        this.onSearchDepUserCallBack('');
      } else if (index == 2) {
        this.userRealName = '';
        this.iIsLimits = '';
      } else if (index == 1) {
        this.userRealName = '';
        this.departId = '';
      }
    },
    //日期格式化
    dateFormat: function dateFormat(date) {
      var date1 = new Date(date);
      var Y = date1.getFullYear();
      var M = date1.getMonth() + 1;
      var D = date1.getDate();
      var newTime = Y + '-' + (M < 10 ? "0" + M : M) + "-" + (D < 10 ? "0" + D : D) + " " + date1.toTimeString().substr(0, 8);
      return newTime;
    },
    handerOk: function handerOk() {
      var _this2 = this;

      // console.log(JSON.stringify(this.backData))
      // console.log(this.userRealName)
      if (this.iIsLimits != "" && this.iIsLimits != undefined) {
        if (this.iIsLimits == 2 && this.departId == "") {
          this.$message.error("请选择部门");
          return;
        }

        if (this.iIsLimits == 3 && this.userRealName == "") {
          this.$message.error("请选择用户");
          return;
        }
      } else {
        this.$message.error("请选择发布范围");
        return;
      }

      var json = {
        iIsLimits: this.iIsLimits,
        userRealName: this.userRealName,
        departId: this.departId,
        tableName: this.backData.table,
        busdataId: this.backData.i_id,
        functionId: this.backData.i_bus_function_id //发布数据

      };
      postAction(this.url.commitPer, json).then(function (res) {
        if (res == "success") {
          var params = {};
          params.table = _this2.backData.table;
          params.i_id = _this2.backData.i_id;
          params.i_is_publish = 1; //发布状态 1 是 0 否

          params.d_datetime2 = _this2.dateFormat(new Date());
          params.d_datetime1 = _this2.backData.d_datetime1;
          params.i_bigint3 = _this2.iIsLimits;

          if (_this2.iIsLimits == 2 && _this2.departId != "") {
            params.s_varchar3 = _this2.departId;
          }

          if (_this2.iIsLimits == 3 && _this2.userRealName != "") {
            params.s_varchar3 = _this2.userRealName;
          } //更改发布状态


          postAction(_this2.url.updateBusdata, params).then(function (res) {});

          _this2.$message.success("发布成功");
        } else {
          _this2.$message.error("发布失败");
        }
      });
    },
    //取消发布
    unpublish: function unpublish() {
      var _this3 = this;

      var params = {};
      params.table = this.backData.table;
      params.i_id = this.backData.i_id; // params.i_busdata_id = this.backData.i_id;

      params.i_bus_function_id = this.backData.i_bus_function_id;
      params.i_is_publish = '0';
      params.i_bigint3 = '0';
      params.d_datetime2 = this.dateFormat(new Date());
      postAction(this.url.updateBusdata, params).then(function (res) {
        //修改权限表发布数据并修改发布范围字段
        if (res.success) {
          // params.table = this.backData.table+"_permit";
          postAction(_this3.url.deletePremit, {
            'deletePremit': params
          }).then(function (res) {
            if (res.success) {
              _this3.$message.success("取消发布成功");
            } else {
              _this3.$message.error("取消发布失败");
            }
          });
        } else {
          _this3.$message.error("取消发布失败");
        }
      });
    }
  }
};
