import { httpAction } from '@/api/manage';
import { getAction, postAction } from '@/api/manage';
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import Vue from 'vue';
export default {
  name: "BusFunctionCatModal",
  mixins: [JeecgListMixin],
  data: function data() {
    return {
      isshowType: '1',
      typeModel: '',
      //范围类型    需要判断  分别值有 0 1 2 3
      checkedDepartNameString: '',
      confirmLoading: false,
      selectvalue: '1',
      //是否选中
      itypeId: '',
      itypeName: '',
      title: "操作",
      visible: false,
      selectedRole: '',
      //
      selectedUser: '',
      //
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      }
    };
  },
  created: function created() {},
  mounted: function mounted() {},
  methods: {
    showCat2: function showCat2(record) {
      var _this = this;

      this.typeModel = record.spermitType;
      this.$nextTick(function () {
        if (record.spermitType == '1') {
          _this.isshowType = 1;
          _this.selectedRole = record.itypeName;
          _this.typeModel = '角色';
        } else if (record.spermitType == '3') {
          _this.isshowType = 3;
          _this.selectedUser = record.itypeName;
          _this.typeModel = '人员';
        } else if (record.spermitType == '2') {
          _this.isshowType = 2;
          _this.typeModel = '部门';
          _this.checkedDepartNameString = record.itypeName;
        } else {
          _this.isshowType = 0;
          _this.typeModel = '所有人';
        }

        _this.selectvalue = Number(record.sdisplay);
        _this.visible = true;
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
