import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "BusFunctionViewModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      typeModel: '',
      roleId: '',
      roleList: [],
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaBus/busFunctionView/add",
        edit: "/oaBus/busFunctionView/edit"
      }
    };
  },
  created: function created() {
    this.initialRoleList();
  },
  methods: {
    initialRoleList: function initialRoleList() {
      var _this = this;

      var url = "/sys/role/queryall";
      getAction(url).then(function (res) {
        //console.log(res.result);
        _this.roleList = res.result;
      });
    },
    add: function add() {
      this.visible = true;
    },
    close: function close() {
      /*document.getElementById("#stypeModel").empty();*/
      this.typeModel = '';
      this.roleId = '';
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var flag = true;

      if (this.typeModel == '' || this.typeModel == null) {
        flag = false;
      }

      if (this.roleId == '' || this.roleId == null) {
        flag = false;
      }

      if (flag) {
        var roleObj = {
          "itype": this.typeModel,
          "iroleId": this.roleId
        };
        this.$emit("ok", roleObj);
        this.close();
      }
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
