import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
import Vue from 'vue';
import axios from 'axios';
import { ACCESS_TOKEN } from "../../../store/mutation-types";
import { deleteAction, getAction, downFile } from '@/api/manage';
export default {
  name: "OaTemplateModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      headers: {
        'X-Access-Token': Vue.ls.get(ACCESS_TOKEN)
      },
      upFileName: '',
      upFailId: 0,
      downFilePath: '',
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      selectData: [],
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {// iId:{rules: [{ required: true, message: '请输入主键id!' }]},
      },
      url: {
        add: "/papertitle/oaTemplate/add",
        edit: "/papertitle/oaTemplate/edit",
        download: "/papertitle/oaTemplate/download"
      }
    };
  },
  created: function created() {
    this.getSelection();
  },
  methods: {
    add: function add(record) {
      var _this = this;

      if (record.ifileId) {
        this.upFailId = 1;
      } else {
        this.upFailId = 0;
      }

      this.getFileNameById(record.ifileId);
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'itype', 'sname', 'ifileId', 'ititleRuleId', 'sremarks'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.upFileName = '';
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    getFileNameById: function getFileNameById(id) {
      var that = this;
      var url = "/papertitle/oaTemplate/getFileNameById";
      getAction(url, {
        ifileId: id
      }).then(function (res) {
        console.log(res.result);
        that.upFileName = res.result.sfileName;
        that.downFilePath = res.result.sfilePath;
      });
    },
    getSelection: function getSelection() {
      var _this2 = this;

      var url = "/papertitle/paperTitleSetting/paperTitleList";
      getAction(url).then(function (res) {
        _this2.selectData = res.result;
      });
    },
    downFileName: function downFileName() {
      var url = window._CONFIG['domianURL'] + this.url.download;
      window.open(url + "/" + this.downFilePath);
    }
  }
};
