import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
export default {
  name: "OaProcOpinionCopyModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      // validatorRules:{
      //   iId:{rules: [{ required: true, message: '请输入主键id!' }]},
      // },
      url: {
        // add: "/oaprocopinion/oaProcOpinion/add",
        // edit: "/oaprocopinion/oaProcOpinion/edit",
        copy: "/oaprocopinion/oaProcOpinion/copeConfig"
      }
    };
  },
  created: function created() {},
  methods: {
    // add () {
    //   this.edit({});
    // },
    // edit
    copy: function copy(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      console.log(this.model);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'sprocOpinionName', 'procDefKey')); //时间格式化

      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = ''; // if(!this.model.iid){
          //   httpurl+=this.url.add;
          //   method = 'post';
          // }else{

          httpurl += _this2.url.copy;
          method = 'post'; // }

          var formData = Object.assign(_this2.model, values); //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
