import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "OaOpinionSetModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      data: [],
      buttonInfo: '',
      ibuttonId: '',
      iprocOpinionId: '',
      taskDefKey: [],
      TaskLink: [],
      //流程定义Id  //任务环节列表
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaopinionset/oaOpinionSet/add",
        edit: "/oaopinionset/oaOpinionSet/edit",
        buttonList: "/oabutton/oaButton/buttonList",
        queryById: "/oabutton/oaButton/queryById",
        TaskLink: "/wf/process/actsList"
      }
    };
  },
  created: function created() {// this.getButtonList();
  },
  methods: {
    add: function add(TaskLinkId, iprocOpinionId) {
      var _this = this;

      this.model.iprocOpinionId = iprocOpinionId;
      this.form.resetFields(); //---------------任务环节列表---------------

      getAction(this.url.TaskLink, {
        processDefinitionId: TaskLinkId
      }).then(function (res) {
        _this.TaskLink = res.result;
      });
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'iprocOpinionId', 'iprocSetId', 'itaskOpinionName', 'itaskOpinionOrder', 'taskDefKey', 'procDefKey')); //时间格式化

      });
    },
    edit: function edit(record, TaskLinkId) {
      var _this2 = this;

      this.form.resetFields();
      this.model = Object.assign({}, record); //---------------任务环节列表---------------

      getAction(this.url.TaskLink, {
        processDefinitionId: TaskLinkId
      }).then(function (res) {
        _this2.TaskLink = res.result;
      });
      this.ibuttonId = this.model.ibuttonId; //分割-数据=数组

      this.taskDefKey = this.model.taskDefKey.split(',');
      this.visible = true;
      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, 'iid', 'iprocOpinionId', 'iprocSetId', 'itaskOpinionName', 'itaskOpinionOrder', 'taskDefKey', 'procDefKey')); //时间格式化

      });
    },
    // getButtonList(){//按钮列表
    //   getAction(this.url.buttonList,{}).then(res=>{
    //     this.data = res.result;
    //   });
    // },
    getButtonId: function getButtonId() {
      //按钮-赋值
      this.model.ibuttonId = this.ibuttonId;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this3 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this3.model.iid) {
            httpurl += _this3.url.add;
            method = 'post';
          } else {
            httpurl += _this3.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this3.model, values); //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handelChange: function handelChange(value) {
      console.log("----------任务环节赋值-------------" + value);
      this.model.taskDefKey = value.toString();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
