import { httpAction } from '@/api/manage';
import { getAction, postAction } from '@/api/manage';
export default {
  name: "DelTime",
  data: function data() {
    return {
      title: "修改附件名称",
      visible: false,
      model: {},
      fileData: {
        i_id: '',
        s_file_name: '',
        s_file_path: ''
      },
      fileName: '',
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      url: {}
    };
  },
  created: function created() {},
  methods: {
    show: function show(file) {
      this.fileData.i_id = file.iid;
      this.fileData.s_file_name = file.sfileName.slice(0, file.sfileName.lastIndexOf('.'));
      this.fileData.s_file_path = file.sfilePath;
      this.fileName = this.fileData.s_file_name;
      this.visible = true;
    },
    close: function close() {
      this.$emit('close');
      this.fileData = {};
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this = this;

      this.fileData.s_file_name = this.fileName;
      postAction('/oaBus/oaFile/updateFileName', this.fileData).then(function (res) {
        _this.close();

        _this.$emit("reloadAfterUpdate");
      }); // this.close()
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
