import { getAction, postAction } from "../../../api/manage";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import DictItemList from "../../system/DictItemList";
export default {
  name: "nextUsers",
  mixins: [JeecgListMixin],
  components: {
    DictItemList: DictItemList
  },
  data: function data() {
    return {
      title: '下一任务',
      okText: '确定',
      //控制组件数据可见
      isMul: false,
      //下一任务是单选/多选
      isDept: false,
      endType: false,
      //是否是结束节点
      nextsActs: [],
      //节点信息
      activity: '',
      endTime: '',
      //穿梭框数据
      mockData: [],
      //部门信息
      deptsList: [],
      //部门选择集合
      departSelect: {},
      //部门对应用户id
      departUsersId: {},
      departUsersMsg: {},
      //环节分类，从属于 排他，并行，包容网关
      gateWayTypeSelect: {
        parallel: {},
        inclusive: {}
      },
      typeCount: {
        normal: [],
        inclusive: [],
        parallel: []
      },
      preCilck: null,
      timeCheck: false,
      //是否限制时间
      columns: [{
        title: '用户姓名',
        align: "center",
        dataIndex: 'uname'
      }, {
        title: '所在部门',
        align: "center",
        dataIndex: 'dname'
      }],
      columns2: [{
        title: '部门名称',
        align: "center",
        dataIndex: 'departName'
      }],
      dataSource: [],
      visible: false,
      confirmLoading: false,
      urls: {
        departUsetQuery: '/oaBus/taskInAct/deptUsersQuery'
      }
    };
  },
  computed: {},
  methods: {
    // getCheckboxProps(res){
    //   return res=>({
    //     props: {
    //       defaultChecked:res.uid===this.dataSource[0].uid
    //     }
    //   })
    // },
    departClickCheck: function departClickCheck(res) {
      var _this = this;

      return {
        on: {
          click: function click() {
            var rowkeys = _this.selectedRowKeys;
            var selectionRows = _this.selectionRows;

            if (rowkeys.length > 0 && rowkeys.includes(res.id)) {
              rowkeys.splice(rowkeys.indexOf(res.id), 1);
              selectionRows.splice(rowkeys.indexOf(res), 1);
            } else {
              rowkeys.push(res.id);
              selectionRows.push(res);
            }

            _this.selectedRowKeys = rowkeys;
            _this.selectionRows = selectionRows;
          }
        }
      };
    },
    setRowRadio: function setRowRadio(res) {
      var _this2 = this;

      return {
        on: {
          click: function click() {
            var keys = [];
            keys.push(res.uid);
            _this2.selectedRowKeys = keys;
          }
        }
      };
    },
    setRowCheck: function setRowCheck(res) {
      var _this3 = this;

      return {
        on: {
          click: function click() {
            var rowkeys = _this3.selectedRowKeys;

            if (rowkeys.length > 0 && rowkeys.includes(res.uid)) {
              rowkeys.splice(rowkeys.indexOf(res.uid), 1);
            } else {
              rowkeys.push(res.uid);
            }

            _this3.selectedRowKeys = rowkeys;
          }
        }
      };
    },
    // onSelectChange(e,d) {
    //   // alert(JSON.stringify(d))
    //   this.selectedRowKeys = e;
    // },
    onChangeCheck: function onChangeCheck(item) {
      var id = item.target.value.split('-')[0];
      var type = item.target.value.split('-')[1]; //修改数据属性(修改选中状态用来向左边传)

      for (var i in this.departSelect[type]) {
        if (id == this.departSelect[type][i].id) {
          this.departSelect[type][i].checked = true;
        }
      }
    },
    showNextUsers: function showNextUsers(nextsActs) {
      if (nextsActs.length > 0 && nextsActs[0].actMsg.type == 'endEvent') {
        this.endType = true;
        this.title = '办结';
        this.okText = '办结';
        this.activity = nextsActs[0];
      } else {
        this.nextsActs = nextsActs; //统计数量
        //---------默认选择 下一任下第一个环节----------

        this.clickAct(this.nextsActs[0]);
        var _iteratorNormalCompletion = true;
        var _didIteratorError = false;
        var _iteratorError = undefined;

        try {
          for (var _iterator = nextsActs[Symbol.iterator](), _step; !(_iteratorNormalCompletion = (_step = _iterator.next()).done); _iteratorNormalCompletion = true) {
            var i = _step.value;
            var inclusiveGateway = i.actMsg.inclusiveGateway;
            var parallelGateway = i.actMsg.parallelGateway;

            if (inclusiveGateway) {
              this.typeCount.inclusive.push(i);
            } else if (parallelGateway) {
              this.typeCount.parallel.push(i);
            } else {
              this.typeCount.normal.push(i);
            }
          }
        } catch (err) {
          _didIteratorError = true;
          _iteratorError = err;
        } finally {
          try {
            if (!_iteratorNormalCompletion && _iterator.return != null) {
              _iterator.return();
            }
          } finally {
            if (_didIteratorError) {
              throw _iteratorError;
            }
          }
        }
      }

      this.visible = true;
    },
    //点击某一节点---选择节点相关信息
    clickAct: function clickAct(item) {
      /**
       *  (包容/并行网关)记录上一节点的选择信息
       *  TODO 方式待修改
       */
      var preType = null;

      if (this.preCilck != null) {
        preType = this.preCilck.oaProcActinst.userOrRole == 'dept';
      }

      if (this.preCilck != null && (preType && Object.keys(this.departSelect).length > 0 || !preType && this.selectionRows.length > 0)) {
        //判断类型是并行还是包容//并记录选择的内容
        var select = {};
        select['activity'] = this.preCilck;

        if (preType) {
          select['deptsList'] = this.deptsList;
          select['departSelect'] = this.departSelect;
          select['departUsersId'] = this.departUsersId;
          select['mockData'] = this.mockData;
        }

        if (!preType) {
          select['selectedRowKeys'] = this.selectedRowKeys;
          select['selectionRows'] = this.selectionRows;
        }

        if (this.preCilck.actMsg.inclusiveGateway) {
          this.gateWayTypeSelect.inclusive[this.preCilck.actMsg.id] = select;
        }

        if (this.preCilck.actMsg.parallelGateway) {
          this.gateWayTypeSelect.parallel[this.preCilck.actMsg.id] = select;
        }
      } //清空旧的选择


      this.selectedRowKeys = [];
      this.selectionRows = []; //查看节点信息
      //复选框还是单选

      this.isMul = item.oaProcActinst.multAssignee; //是否选部门

      this.isDept = item.oaProcActinst.userOrRole == 'dept'; //判断当前节点是否是包容/并行网关 之前是否记录过它的选择信息,记录过就使用

      if (item.actMsg.inclusiveGateway || item.actMsg.parallelGateway) {
        //包容/并行
        var currentAct = this.gateWayTypeSelect.inclusive[item.actMsg.id];

        if (currentAct != undefined && !this.isDept) {
          this.selectedRowKeys = currentAct['selectedRowKeys'];
          this.selectionRows = currentAct['selectionRows'];
        }
      }

      if (this.isDept) {
        //判断是否是包容/并行
        var _currentAct = this.gateWayTypeSelect.inclusive[item.actMsg.id];

        if ((item.actMsg.inclusiveGateway || item.actMsg.parallelGateway) && _currentAct != undefined) {
          this.deptsList = _currentAct['deptsList'];
          this.departSelect = _currentAct['departSelect'];
          this.departUsersId = _currentAct['departUsersId'];
          this.mockData = _currentAct['mockData'];
        } else {
          this.mockData = item.nextUsers;
          this.deptsList = item.oaProcActinst.deptsList;

          for (var x in this.deptsList) {
            //构造备选集合
            this.departSelect[this.deptsList[x]] = []; //部门选择用户集合构造

            this.departUsersId[this.deptsList[x]] = []; //备选用户完整信息

            this.departUsersMsg[this.deptsList[x]] = [];
          }
        }
      } //节点信息


      this.activity = item; //记录上一环节

      this.preCilck = item;
      this.dataSource = item.nextUsers;

      if (!this.isDept && this.dataSource.length > 0) {
        //默认选中表格第一个--------------------------------------
        var defaultSelected = [];
        defaultSelected.push(this.dataSource[0].uid);
        this.selectedRowKeys = defaultSelected;
      }
    },
    confirm: function confirm() {
      //时间校验
      this.timeCheckMethod(); //校验并行与包容

      var palllen = this.typeCount.parallel.length;
      var ialllen = this.typeCount.inclusive.length;

      if (palllen == 0 && ialllen == 0) {
        //非包容/并行
        this.singleType();
      } else {
        //包容/并行
        this.gateWayCheck();
        this.moreThanOneType();
      }
    },
    //并行或包容
    moreThanOneType: function moreThanOneType() {
      console.log('      包容 ::::', JSON.stringify(this.gateWayTypeSelect));
      this.$emit('confirmNextUsersMore', this.gateWayTypeSelect, this.endTime);
    },
    //时间校验
    timeCheckMethod: function timeCheckMethod() {
      if (this.timeCheck) {
        if (this.endTime == '') {
          this.$message.error('请选择办理结束时间');
          return;
        }

        if (new Date() > new Date(this.endTime)) {
          this.$message.error('办理结束时间不得小于当前时间');
          return;
        }
      }
    },
    //包容/并行网关校验
    gateWayCheck: function gateWayCheck() {
      var plen = Object.keys(this.gateWayTypeSelect.parallel).length;
      var ilen = Object.keys(this.gateWayTypeSelect.inclusive).length;
      var palllen = this.typeCount.parallel.length;
      var ialllen = this.typeCount.inclusive.length;

      if (palllen == 0 && ialllen == 0) {
        return;
      }

      if (plen != palllen) {
        this.$message.error('当前的所有环节都必须选择用户');
        return;
      }

      if (plen == palllen && palllen != 0) {
        var res = window.confirm("已选择所有环节是否检查所选用户");

        if (res) {
          return;
        } //校验用户


        this.moreThanOneUserCheck('parallel');
      }

      if (ilen != ialllen) {
        if (ilen == 0) {
          this.$message.error('您没有选择环节或选择的部门没有可办理用户请检查');
          return;
        }

        var res = window.confirm("可以选择所有环节是否继续选择");

        if (res) {
          return;
        } //校验选择的用户


        this.moreThanOneUserCheck('inclusive');
      }

      if (ilen == ialllen && ialllen != 0) {
        var res = window.confirm("已选择所有环节是否检查所选用户");

        if (res) {
          return;
        }

        this.moreThanOneUserCheck('inclusive');
      }
    },
    moreThanOneUserCheck: function moreThanOneUserCheck(type) {
      var data = null;

      if (type == 'inclusive') {
        data = this.gateWayTypeSelect.inclusive;
      }

      if (type == 'parallel') {
        data = this.gateWayTypeSelect.parallel;
      }

      for (var index in data) {
        var d = data[index];
        var isDept = d.activity.actMsg.userOrRole == 'dept';

        if (isDept) {
          //部门类型
          var dep = d.departUsersId;

          for (var k in dep) {
            var v = dep[k];

            if (k.indexOf("主办") >= 0) {
              if (v.length <= 0) {
                this.$message.error('主办部门必须选择办理人');
                return;
              }
            }
          }
        } else {
          //用户类
          if (d.selectedRowKeys.length <= 0) {
            this.$message.error(d.activity.actMsg.name + '环节，请选择办理人');
            return;
          }
        }
      }
    },
    //非包容/并行网关办理方式
    singleType: function singleType() {
      var ids = [];

      if (this.isDept) {
        var _ids = [];
        var depMSg = {};

        for (var i in this.departUsersId) {
          if (i.indexOf("主办") >= 0 && this.departUsersId[i].length == 0) {
            this.$message.error(i + "部门必须选择用户");
            return;
          }

          if (this.departUsersId[i].length > 0) {
            _ids.push(this.departUsersId[i]);

            depMSg[i] = this.departUsersId[i];
          }
        }

        console.log('==========部门：：：', _ids, JSON.stringify(this.activity)); //校验

        this.$emit('func', _ids, this.activity, this.endTime, depMSg);
        this.cancel();
      } else if (this.endType) {
        this.$emit('func', ids, this.activity, this.endTime);
        this.cancel();
      } else {
        //办理人校验
        if (this.selectedRowKeys.length <= 0) {
          this.$message.error("请选择办理人");
          return;
        }

        ids = this.selectedRowKeys;
        console.log('------普通：' + ids, JSON.stringify(this.activity)); //办理流程

        this.$emit('func', ids, this.activity, this.endTime);
        this.cancel();
      }
    },
    //穿梭方法实现
    toRight: function toRight(item) {
      //从部门列表中移除--添加到对应的list中
      //1 主办部门只能有一个
      if (item.indexOf("主办") >= 0) {
        //判断是否已经有元素了
        if (this.departSelect[item].length == 0 && this.selectionRows.length > 1 || this.departSelect[item].length > 0) {
          this.$message.error("主办部门只允许有一个");
          return;
        }
      } //2 添加到右边--移除左边


      var del = {};

      for (var i in this.selectionRows) {
        this.selectionRows[i].checked = false; //右边

        this.departSelect[item].push(this.selectionRows[i]); //移除元素

        del[this.selectionRows[i].id] = 1;
      } //移除元素(采用重新赋值的方式)


      var mockData2 = [];

      for (var j = 0; j < this.mockData.length; j++) {
        if (del[this.mockData[j].id] != 1) {
          mockData2.push(this.mockData[j]);
        }
      }

      this.mockData = mockData2; //清空所选

      this.selectionRows = [];
      this.selectedRowKeys = []; // 更新用户显示

      this.currentDeptUserQuery(item);
    },
    toLeft: function toLeft(item) {
      //添加到部门列表中--从list中移除
      var right = [];

      for (var i in this.departSelect[item]) {
        var itt = this.departSelect[item][i];

        if (itt.checked) {
          itt.checked = false;
          this.mockData.push(itt);
        } else {
          right.push(itt);
        }
      } //改变右边


      this.departSelect[item] = right; // 更新用户显示

      this.currentDeptUserQuery(item);
    },

    /**
     * 选中部门的用户查询
     * @param item
     */
    currentDeptUserQuery: function currentDeptUserQuery(item) {
      var _this4 = this;

      var role = this.activity.oaProcActinst.candidates;
      var depts = this.departSelect;
      var param = {};
      param.role = role;
      param.depts = depts;

      if (depts[item].length == 0) {
        this.departUsersId[item] = [];
        this.departUsersMsg[item] = [];
        var keys = [];
        var rows = [];
        this.onSelectChange(keys, rows);
        return;
      } //查询部门对应的用户id 分组


      postAction(this.urls.departUsetQuery, param).then(function (res) {
        if (res.success) {
          if (res.result[item].length == 0) {
            _this4.$message.error('所选' + item + '部门没有对应角色的用户');

            _this4.departUsersId[item] = [];
            _this4.departUsersMsg[item] = [];
            var _keys = [];
            var _rows = [];

            _this4.onSelectChange(_keys, _rows);

            return;
          } else {
            var ids = [];

            for (var i in res.result[item]) {
              ids.push(res.result[item][i].id);
            }

            _this4.departUsersId[item] = ids;
            _this4.departUsersMsg[item] = res.result[item];
            var _keys2 = [];
            var _rows2 = [];

            _this4.onSelectChange(_keys2, _rows2);
          }
        } else {
          _this4.$message.error(res.message);

          return;
        }
      });
    },
    cancel: function cancel() {
      //初始数据
      this.dataInit();
      this.$emit('close');
      this.visible = false;
    },
    onChange: function onChange(date, dateString) {
      this.endTime = dateString;
    },
    changeChoice: function changeChoice(e) {
      if (e.target.value == 1) {
        //不限时间
        this.timeCheck = false;
      } else {
        this.timeCheck = true;
      }
    },
    dataInit: function dataInit() {
      this.selectionRows = [];
      this.selectedRowKeys = [];
      this.endTime = '';
    },

    /**
     * 全选/不选
     */
    onCheckAllChange: function onCheckAllChange(e) {
      var checked = e.target.checked;
      var type = e.target.id;

      if (checked) {
        //修改数据属性
        for (var i in this.departSelect[type]) {
          this.departSelect[type][i].checked = true;
          this.$refs[type][i].checked = true;
        }
      } else {
        for (var i in this.departSelect[type]) {
          this.departSelect[type][i].checked = false;
          this.$refs[type][i].checked = false;
        }
      }
    }
  }
};
