import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "DisFileTestModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iid: {
          rules: [{
            required: true,
            message: '请输入发文ID!'
          }]
        },
        tdraftdate: {
          rules: [{
            required: true,
            message: '请输入拟稿日期!'
          }]
        }
      },
      url: {
        add: "/oaDisFileTest/disFileTest/add",
        edit: "/oaDisFileTest/disFileTest/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'stitle', 'stitleparam', 'stitledept', 'ssafetylevel', 'iuserid', 'susername', 'ideptid', 'sdeptname', 'sphone', 'tdraftdate', 'sfilemark', 'skeyword', 'snumber', 'scollate', 'stypewrite', 'iprintnum', 'tsealldate', 'sbackgroup', 'sovert', 'scountersign', 'scrsdeptids', 'scrsdept', 'ssettype', 'smainunitids', 'smainunitnames', 'sccunitids', 'sccunitnames', 'sinsideids', 'sinsidedeptname', 'sreportids', 'sreportnames', 'surgency', 'saffairsovert', 'saccount', 'tissuedate', 'tupdate', 'spapername', 'sfiletype', 'sstate', 'sremarks', 'itimelimit', 'ipaginalnum', 'sreason', 'sredundance', 'sredundance1', 'processinstid', 'idocid', 'itemplateid', 'sissueuser', 'dzhcnissuedate', 'sstandardflag', 'sprovissuedcityids')); //时间格式化


        _this.form.setFieldsValue({
          dissuedate: _this.model.dissuedate ? moment(_this.model.dissuedate) : null
        });
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          formData.dissuedate = formData.dissuedate ? formData.dissuedate.format() : null;
          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
