import { httpAction, getAction, putAction, postAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
import JSelectUserByDep from '@/components/jeecgbiz/JSelectUserByDep';
export default {
  name: "oaCalendarModal",
  inject: ['reload'],
  components: {
    JSelectUserByDep: JSelectUserByDep
  },
  data: function data() {
    return {
      ischeck: false,
      // 判断进入模态框此时状态   是编辑还是查看  默认为编辑
      richengData: '',
      //日程办公数据回显数据
      userlistid: '',
      //所选人员id  为字符串拼接
      usernamelistsarr: '',
      //所选人员 名称拼接    之前提交ids 字符拼接  现改为names 字符拼接
      selectusernames: [],
      userRealName: '',
      userIdLists: [],
      dstartTime: '',
      dendTime: '',
      userList: [],
      title: "查看",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaBus/Calendar/oaCalendar/add",
        edit: "/oaBus/Calendar/oaCalendar/edit"
      },
      username: ''
    };
  },
  created: function created() {
    var userinfo = JSON.parse(localStorage.getItem('userdata')).userInfo;
    console.log(this.userRealName);
    this.username = userinfo.username;
    this.initialUserList();
  },
  methods: {
    //日程办公回显
    dayAnnalysis1: function dayAnnalysis1(data) {
      var _this = this;

      this.visible = true;
      this.userIdLists = Array.from(data.suserNameid);
      this.form.resetFields();
      this.model = Object.assign({}, data);
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'stitle', 'iid', 'suserNames', 'saddress', 'iisTop', 'iisLeader', 'iremindType', 'iopenType', 'dStartTime', 'dEndTime', 'screateBy', 'ibusModelId', 'ibusFunctionId', 'ifunDataId')); //时间格式化


        console.log(_this.model.suserNames);

        _this.form.setFieldsValue({
          dStartTime: _this.model.dStartTime ? moment(_this.model.dStartTime) : null
        });

        _this.form.setFieldsValue({
          dEndTime: _this.model.dEndTime ? moment(_this.model.dEndTime) : null
        }); // this.form.setFieldsValue({suserNames:this.model.suserNames.replace(/,/g,'')})


        _this.form.setFieldsValue({
          suserNames: _this.model.suserNames
        }); // this.form.setFieldsValue({suserNames:this.model.suserNames.substring(0,this.model.suserNames.lastIndexOf(',')).split(',')})

      });
    },
    // 我的日程回显  record   回显方法调用来自于jeecgListMixin.js
    edit: function edit(record) {
      var _this2 = this;

      console.log(record);

      if (typeof record != 'boolean') {
        if (this.username != record.screateBy) {
          this.$message.error('您没有权限编辑他人日程');
          return false;
        }
      }

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        console.log(_this2.model.suserNames);

        _this2.form.setFieldsValue(pick(_this2.model, 'stitle', 'iid', 'suserId', 'suserNames', 'saddress', 'iisTop', 'iisLeader', 'iremindType', 'iopenType', 'dStartTime', 'dEndTime', 'screateBy', 'ibusModelId', 'ibusFunctionId', 'ifunDataId')); //时间格式化


        _this2.form.setFieldsValue({
          dStartTime: _this2.model.dStartTime ? moment(_this2.model.dStartTime) : null
        });

        _this2.form.setFieldsValue({
          dEndTime: _this2.model.dEndTime ? moment(_this2.model.dEndTime) : null
        });

        _this2.form.setFieldsValue({
          suserNames: _this2.model.suserNames
        });

        console.log(_this2.model.suserNames); // this.form.setFieldsValue({suserNames:this.model.suserNames.substring(0,this.model.suserNames.lastIndexOf(',')).split(',')})
        //  this.form.setFieldsValue({suserNames:this.model.suserNames})
      });
    },
    //获取开始时间
    getStartTime: function getStartTime(a) {
      console.log(a._d);
      this.model.dStartTime = a._d;
      ;
    },
    //获取结束时间
    getEndTime: function getEndTime(a) {
      console.log(a._d);
      this.model.dEndTime = a._d;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    timer: function timer(timeval) {
      var date1 = new Date(timeval);
      var Y = date1.getFullYear();
      var M = date1.getMonth() + 1;
      var D = date1.getDate();
      var newTime = Y + '-' + (M < 10 ? "0" + M : M) + "-" + (D < 10 ? "0" + D : D) + " " + date1.toTimeString().substr(0, 8);
      console.log(newTime);
      return newTime;
    }
  }
};
