import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "BusModelModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      dbtable: [],
      checkTrue: 0,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      busDataTable: '',
      busPermit: '',
      busOpinion: '',
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaBus/busModel/add",
        edit: "/oaBus/busModel/edit",
        getDbtable: "/sys/dict/getDictItems/dbtable"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      getAction(this.url.getDbtable).then(function (res) {
        _this.dbtable = res.result;
      });
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iorder', 'sname', 'senName', 'sbusdataTable', 'iisRadio')); //时间格式化

      });
    },
    close: function close() {
      this.busPermit = '';
      this.busOpinion = '';
      this.$emit('close');
      this.visible = false;
    },
    updateOher: function updateOher() {
      this.busPermit = this.busDataTable + '_permit';
      this.busOpinion = this.busDataTable + '_opinion';
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.iid) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
