import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "BusFunctionProcButtonModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      procOpinion: '',
      procButton: '',
      procDefKey: '',
      nowKey: '',
      procOpinionObj: {},
      procButtonObj: {},
      procDefKeyObj: {},
      procDefKeyList: [],
      procOpinionList: [],
