      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaBus/busFunctionView/add",
        edit: "/oaBus/busFunctionView/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    butChange: function butChange(index, item) {
      var num = item.data.key;
      this.procButtonObj = this.procButtonList[num];
    },
    changeOpinion: function changeOpinion(index, item) {
      var num = item.data.key;
      this.procOpinionObj = this.procOpinionList[num];
    },
    changeDefKey: function changeDefKey(index, item) {
      console.log(index, item);
      var num = item.data.key;

      for (var entry in this.procDefKeyList) {
        //console.log(this.procDefKeyList[entry])
        if (this.procDefKeyList[entry].key == num) {
          this.procDefKeyObj = this.procDefKeyList[entry];
        }
      }
      /*
      ;*/


      this.procOpinion = '';
      this.procButton = '';
      this.selectButton(this.procDefKey);
      this.selectOpinion(this.procDefKey);
    },
    selectButton: function selectButton(key) {
      var _this = this;

      this.procButtonList = [];
      var url = "/oaprocbutton/oaProcButton/queryProcButton"; //console.log(this.procDefKey) ;  ,{procKey: this.procDefKey}

      getAction(url, {
        key: key
      }).then(function (res) {
        _this.procButtonList = res.result;
      });
    },
    selectOpinion: function selectOpinion(key) {
      var _this2 = this;

      this.procOpinionList = [];
      var url = "/oaBus/busFunction/queryProcOpinion"; //console.log(this.procDefKey) ;  ,{procKey: this.procDefKey}

      getAction(url, {
        key: key
      }).then(function (res) {
        _this2.procOpinionList = res.result;
      });
    },
    initProcDef: function initProcDef(resolve, reject) {
      var _this3 = this;

      var url = "/wf/process/defKv";
      getAction(url).then(function (res) {
        for (var i in res) {
          var v = res[i];

          _this3.procDefKeyList.push({
            'key': i,
            'val': v
          });
        }

        resolve(_this3.procDefKeyList);
      });
    },
    add: function add(params) {
      var _this4 = this;

      this.nowKey = new Date().valueOf();
      var that; //this.initProcDef();

      /*.catch(function (reason) {
        console.log("失败L"+reason)
      })*/

      if (params != undefined) {
        new Promise(this.initProcDef).then(function (result) {
          console.log(JSON.stringify(result));
          console.log(params.procDefKey);
          /*for(var entry in this.procDefKeyList){
            console.log(this.procDefKeyList[entry])
            if(this.procDefKeyList[entry].key == params.procDefKey ){
              this.procDefKeyObj = this.procDefKeyList[entry]
            }
          }*/

          for (var entry in result) {
            if (result[entry].val == params.procDefKey) {
              _this4.procDefKeyObj = result[entry];
              console.log(_this4.procDefKeyObj);
            }
          }
        });
        this.procDefKey = params.procDefKey;
        this.selectButton(this.procDefKey);
        this.selectOpinion(this.procDefKey);
      }
      /* this.procButton = params.iprocButtonId ;
       this.procOpinion = params.iprocOpinionId ;*/


      this.visible = true; //this.edit({});
    },
    edit: function edit(record) {
      var _this5 = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this5.form.setFieldsValue(pick(_this5.model, 'iId', 'iBusFunctionId', 'iType', 'iRoleId')); //时间格式化

      });
    },
    close: function close() {
      this.procDefKey = '';
      this.procOpinion = '';
      this.procButton = '';
      this.procDefKeyList = [];
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var bol = true;

      if (this.procOpinion == '' || this.procOpinion == null) {
        alert("请选择流程对应的意见！！！");
        bol = false;
      }

      if (this.procButton == '' || this.procButton == null) {
        alert("请选择流程对应的按钮！！！");
        bol = false;
      }

      if (this.procDefKey == '' || this.procDefKey == null) {
        alert("请选择流程！！！");
        bol = false;
      }

      if (bol) {
        var procButtonOpinion = {
          'procDefKey': this.procDefKeyObj,
          'iprocButtonId': this.procButtonObj,
          'iprocOpinionId': this.procOpinionObj
        };
        this.$emit("ok", procButtonOpinion);
        this.close();
      }
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
