import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { getAction } from '@/api/manage'; // 引入搜索部门弹出框的组件

import { ACCESS_TOKEN } from "@/store/mutation-types";
import { addUser, editUser, queryUserRole, queryall } from '@/api/api';
import { disabledAuthFilter } from "@/utils/authFilter";
import { duplicateCheck } from '@/api/api';
import SelectDepartWindow from "../../system/modules/SelectDepartWindow";
export default {
  name: "DocNumSetModal",
  components: {
    SelectDepartWindow: SelectDepartWindow
  },
  data: function data() {
    return {
      ids: '',
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {},
      url: {
        add: "/papertitle/docNumSet/add",
        edit: "/papertitle/docNumSet/edit",
        userWithDepart: "/papertitle/docNumSet/getDocDepartsList",
        userId: "/sys/user/generateUserId"
      },
      selectedModel: null,
      selectedFunction: null,
      allowClear: false,
      modelData: [],
      functionList: [],
      functionData: [],
      upData: [],
      downData: [],
      officeData: [],
      //部门控件
      departDisabled: false,
      //是否是我的部门调用该页面
      roleDisabled: false,
      //是否是角色维护调用该页面
      modalWidth: 800,
      drawerWidth: 700,
      modaltoggleFlag: true,
      confirmDirty: false,
      selectedDepartKeys: [],
      //保存用户选择部门id
      checkedDepartKeys: [],
      checkedDepartNames: [],
      // 保存部门的名称 =>title
      checkedDepartNameString: "",
      // 保存部门的名称 =>title
      userId: "",
      //保存用户id
      disableSubmit: false,
      userDepartModel: {
        userId: '',
        departIdList: []
      } // 保存SysUserDepart的用户部门中间表数据需要的对象

    };
  },
  created: function created() {
    this.getBusModelList();
    this.getTemplateUp();
    this.getTemplateDown();
    this.getTemplateOffice();
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.selectedModel = record.ibusModelId; //获取子集

      var url = "/papertitle/docNumSet/busFunctionList?ibusModelId=" + this.selectedModel;
      getAction(url).then(function (res) {
        // console.log(res);
        _this.functionList = res.result;
      });
      this.form.resetFields();
      this.userId = record.iid;
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'ibusModelId', 'ibusFunctionId', 'iorder', 'sname', 'sdocRule', 'idocNum', 'iutemplateId', 'idtemplateId', 'iatemplateId', 'sremarks', 'screateBy', 'supdateBy'));
      }); // 调用查询用户对应的部门信息的方法

      this.checkedDepartKeys = [];
      this.loadCheckDeparts();
    },
    //下拉选列表-所属模块
    getBusModelList: function getBusModelList() {
      var _this2 = this;

      var url = "/papertitle/docNumSet/busModelList";
      getAction(url).then(function (res) {
        console.log(res.result);
        _this2.modelData = res.result;
      });
    },
    getModalVal: function getModalVal(value) {
      var _this3 = this;

      // console.log(value);
      var url = "/papertitle/docNumSet/busFunctionList?ibusModelId=" + value;
      getAction(url).then(function (res) {
        _this3.functionList = res.result;

        _this3.form.setFieldsValue({
          ibusFunctionId: ''
        });
      });
    },
    //下拉选列表-上报
    getTemplateUp: function getTemplateUp() {
      var _this4 = this;

      var url = "/papertitle/oaTemplate/templateList?type=1";
      getAction(url).then(function (res) {
        _this4.upData = res.result;
      });
    },
    //下拉选列表-下发
    getTemplateDown: function getTemplateDown() {
      var _this5 = this;

      var url = "/papertitle/oaTemplate/templateList?type=2";
      getAction(url).then(function (res) {
        _this5.downData = res.result;
      });
    },
    //下拉选列表-办公单
    getTemplateOffice: function getTemplateOffice() {
      var _this6 = this;

      var url = "/papertitle/oaTemplate/templateList?type=3";
      getAction(url).then(function (res) {
        _this6.officeData = res.result;
      });
    },
    loadCheckDeparts: function loadCheckDeparts() {
      var _this7 = this;

      var that = this;

      if (!that.userId) {
        return;
      }

      getAction(that.url.userWithDepart, {
        userId: that.userId
      }).then(function (res) {
        that.checkedDepartNames = [];

        if (res.success) {
          for (var i = 0; i < res.result.length; i++) {
            that.checkedDepartNames.push(res.result[i].title);
            _this7.checkedDepartNameString = _this7.checkedDepartNames.join(",");
            that.checkedDepartKeys.push(res.result[i].key);
          }

          that.userDepartModel.departIdList = that.checkedDepartKeys;
        } else {
          console.log(res.message);
        }
      });
    },
    // 搜索用户对应的部门API
    onSearch: function onSearch() {
      this.$refs.SelectDepartWindow.add(this.checkedDepartKeys, this.userId);
    },
    // 获取用户对应部门弹出框提交给返回的数据
    modalFormOk: function modalFormOk(formData) {
      this.checkedDepartNames = [];
      this.selectedDepartKeys = [];
      this.checkedDepartNameString = '';
      this.userId = formData.userId;
      this.userDepartModel.userId = formData.userId;

      for (var i = 0; i < formData.departIdList.length; i++) {
        this.selectedDepartKeys.push(formData.departIdList[i].key);
        this.checkedDepartNames.push(formData.departIdList[i].title);
        this.checkedDepartNameString = this.checkedDepartNames.join(",");
      }

      this.userDepartModel.departIdList = this.selectedDepartKeys;
      this.checkedDepartKeys = this.selectedDepartKeys; //更新当前的选择keys
      // console.log("=====>" + this.checkedDepartKeys);
    },
    refresh: function refresh() {
      this.selectedDepartKeys = [];
      this.checkedDepartKeys = [];
      this.checkedDepartNames = [];
      this.checkedDepartNameString = "";
      this.userId = "";
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.disableSubmit = false;
      this.userDepartModel = {
        userId: '',
        departIdList: []
      };
      this.checkedDepartNames = [];
      this.checkedDepartNameString = '';
      this.checkedDepartKeys = [];
      this.selectedDepartKeys = [];
    },
    handleOk: function handleOk() {
      var _this8 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this8.model.iid) {
            httpurl += _this8.url.add;
            method = 'post';
          } else {
            httpurl += _this8.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this8.model, values);
          formData.ibusModelId = _this8.selectedModel;
          formData.ibusFunctionId = _this8.selectedFunction;
          formData.selecteddeparts = _this8.userDepartModel.departIdList.length > 0 ? _this8.userDepartModel.departIdList.join(",") : '';
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
