        del[this.selectionRows[i].id] = 1;
      } //移除元素(采用重新赋值的方式)


      var mockData2 = [];

      for (var j = 0; j < this.mockData.length; j++) {
        if (del[this.mockData[j].id] != 1) {
          mockData2.push(this.mockData[j]);
        }
      }

      this.mockData = mockData2; //清空所选

      this.selectionRows = [];
      this.selectedRowKeys = []; // 更新用户显示

      this.currentDeptUserQuery(item);
    },
    toLeft: function toLeft(item) {
      //添加到部门列表中--从list中移除
      var right = [];

      for (var i in this.departSelect[item]) {
        var itt = this.departSelect[item][i];

        if (itt.checked) {
          itt.checked = false;
          this.mockData.push(itt);
        } else {
          right.push(itt);
        }
      } //改变右边


      this.departSelect[item] = right; // 更新用户显示

      this.currentDeptUserQuery(item);
    },

    /**
     * 选中部门的用户查询
     * @param item
     */
    currentDeptUserQuery: function currentDeptUserQuery(item) {
      var _this5 = this;

      var role = this.currentClick.oaProcActinst.candidates;
      var depts = this.departSelect;
      var param = {};
      param.role = role;
      param.depts = depts;

      if (depts[item].length == 0) {
        this.departUsersId[item] = [];
        this.departUsersMsg[item] = [];
        var keys = [];
        var rows = [];
        this.onSelectChange(keys, rows);
        return;
      } //查询部门对应的用户id 分组


      postAction(this.urls.departUsetQuery, param).then(function (res) {
        if (res.success) {
          // ////console.log('-------------->>>', JSON.stringify(res.result[item]))
          if (res.result[item].length == 0) {
            _this5.$message.error('所选' + item + '部门没有对应角色的用户');

            _this5.departUsersId[item] = [];
            _this5.departUsersMsg[item] = [];
            var _keys2 = [];
            var _rows = [];

            _this5.onSelectChange(_keys2, _rows);

            return;
          } else {
            var ids = [];

            for (var i in res.result[item]) {
              ids.push(res.result[item][i].id);
            }

            _this5.departUsersId[item] = ids;
            _this5.departUsersMsg[item] = res.result[item];
            var _keys3 = [];
            var _rows2 = [];

            _this5.onSelectChange(_keys3, _rows2); //记录用户选择


            _this5.showPreClick(_this5.currentClick, true, false);
          }
        } else {
          _this5.$message.error(res.message);

          return;
        }
      });
    },
    cancel: function cancel() {
      //初始数据
      this.dataInit();
      this.$emit('close');
      this.visible = false;
    },
    onChange: function onChange(date, dateString) {
      this.endTime = dateString;
    },
    changeChoice: function changeChoice(e) {
      if (e.target.value == 1) {
        //不限时间
        this.timeCheck = false;
      } else {
        this.timeCheck = true;
      }
    },
    dataInit: function dataInit() {
      this.selectionRows = [];
      this.selectedRowKeys = [];
      this.endTime = '';
    },

    /**
     * 全选/不选
     */
    onCheckAllChange: function onCheckAllChange(e) {
      var checked = e.target.checked;
      var type = e.target.id;

      if (checked) {
        //修改数据属性
        for (var i in this.departSelect[type]) {
          this.departSelect[type][i].checked = true;
          this.$refs[type][i].checked = true;
        }
      } else {
        for (var i in this.departSelect[type]) {
          this.departSelect[type][i].checked = false;
          this.$refs[type][i].checked = false;
        }
      }
    }
  }
};
