import { httpAction, getAction, postAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "oaTeamworkInstModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      modelList: [],
      selectList: [],
      teamworkList: [],
      teamworkSetList: [],
      busModelId: '',
      modelId: '',
      functionId: '',
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaTea/oaTeamworkInst/add",
        edit: "/oaTea/oaTeamworkInst/edit",
        selectTaskType: '/oaBus/newTask/findModelPermit1',
        selectTaskDetail: '/oaBus/newTask/findFunctionPermit'
      }
    };
  },
  created: function created() {
    this.getBusModelSelectList();
    this.getTeamWorkList();
  },
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iId', 'iteamworkId', 'iteamworkSetId', 'iorder', 'ibusModelId', 'ibusFunctionId', 'iversion')); //时间格式化

      });
    },
    //获取选值    所属模块value   此value值时所属业务的方法的基本参数
    getModelVal: function getModelVal(e) {
      //获取对应业务列表  所需的模块儿id*/
      // this.selectList = '';
      this.busModelId = e;
      this.getFunctionList(e);
      this.form.setFieldsValue({
        ibusFunctionId: ''
      });
    },
    getBusModelSelectList: function getBusModelSelectList() {
      var _this2 = this;

      getAction(this.url.selectTaskType, {}).then(function (res) {
        _this2.modelList = res.result;
      });
    },
    // 所属业务模块数据  参数依赖于所属模块 所选id
    getFunctionList: function getFunctionList(value) {
      var _this3 = this;

      var url = "/papertitle/docNumSet/busFunctionList?ibusModelId=" + value;
      value = value.toString();
      getAction(this.url.selectTaskDetail, {
        modelId: value
      }).then(function (res) {
        _this3.selectList = res.result;
      });
    },
    getVal: function getVal(e) {
      console.log(e);
      this.getTeamWorkSetList(e);
    },
    getTeamWorkList: function getTeamWorkList() {
      var _this4 = this;

      var url = "/oateamwork/oaTeamwork/findTeamworkName";
      getAction(url, {}).then(function (res) {
        _this4.teamworkList = res.result;
      });
    },
    getTeamWorkSetList: function getTeamWorkSetList(value) {
      var _this5 = this;

      var url = "/oateamwork/oaTeamworkSet/findTeamworkSet?iTeamworkId=" + value;
      getAction(url).then(function (res) {
        _this5.teamworkSetList = res.result;
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this6 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this6.model.id) {
            httpurl += _this6.url.add;
            method = 'post';
          } else {
            httpurl += _this6.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this6.model, values); //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
