import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "OaButtonSetModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      data: [],
      buttonInfo: [],
      // selectButton:'',
      ibuttonId: '',
      TaskLinkId: '',
      taskDefKey: [],
      TaskLink: [],
      //流程定义Id  //任务环节列表
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oabuttonset/oaButtonSet/add",
        edit: "/oabuttonset/oaButtonSet/edit",
        buttonList: "/oabutton/oaButton/buttonList",
        queryById: "/oabutton/oaButton/queryById",
        TaskLink: "/wf/process/actsList"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add(TaskLinkId, iprocButtonId) {
      var _this = this;

      this.model.iprocButtonId = iprocButtonId;
      this.form.resetFields();
      this.getButtonList();
      this.TaskLinkId = TaskLinkId; //---------------任务环节---------------

      getAction(this.url.TaskLink, {
        processDefinitionId: this.TaskLinkId
      }).then(function (res) {
        _this.TaskLink = res.result;
      });
      this.visible = true; // this.edit({});
    },
    edit: function edit(record, TaskLinkId) {
      var _this2 = this;

      this.form.resetFields();
      this.model = Object.assign({}, record); // console.log(this.model);

      this.getButtonList();
      this.TaskLinkId = TaskLinkId; //---------------任务环节---------------

      getAction(this.url.TaskLink, {
        processDefinitionId: this.TaskLinkId
      }).then(function (res) {
        _this2.TaskLink = res.result;
      });
      this.ibuttonId = this.model.ibuttonId;
      this.taskDefKey = this.model.taskDefKey.split(',');
      this.visible = true;
      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, 'iid', 'procdefKey', 'taskDefKey', 'iprocButtonId', 'ibuttonId', 'iorder', 'ipermitType', 'iisCreater', 'iisReader', 'iisLastsender', 'iisTransactors', 'iisDefault', 'itaskDefKey', 'smanagerRoleId')); //时间格式化

      });
    },
    getButtonList: function getButtonList() {
      var _this3 = this;

      //按钮列表
      getAction(this.url.buttonList, {}).then(function (res) {
        _this3.data = res.result;
      });
    },
    getButtonId: function getButtonId() {
      //按钮改变-赋值
      this.model.ibuttonId = this.ibuttonId;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this4 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this4.model.iid) {
            httpurl += _this4.url.add;
            method = 'post';
          } else {
            httpurl += _this4.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this4.model, values); //时间格式化

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handelChange: function handelChange(value) {
      console.log("----------任务环节赋值-------------" + value);
      this.model.taskDefKey = value.toString();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
