import { httpAction, getAction, postAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "oaTeamworkSetModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      modelList: [],
      selectList: [],
      busModelId: '',
      modelId: '',
      findMax: '',
      functionId: '',
      num: '',
      model: {// iteamworkId:'',
        // steamworkName:'',
        // iversion:'',
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oateamwork/oaTeamworkSet/add",
        edit: "/oateamwork/oaTeamworkSet/edit",
        findMax: "/oateamwork/oaTeamworkSet/findMax",
        selectTaskType: '/oaBus/newTask/findModelPermit1',
        selectTaskDetail: '/oaBus/newTask/findFunctionPermit'
      },
      //用来判断弹框是用来新增或是编辑两种状态
      isadd: false
    };
  },
  created: function created() {// this.getBusModelSelectList();
  },
  methods: {
    add1: function add1(iteamworkId, steamworkName, iversion) {
      var _this = this;

      this.isadd = true;
      this.model.iteamworkId = iteamworkId;
      this.model.iversion = Number(iversion);
      this.visible = true;
      this.title = steamworkName;
      this.form.resetFields(); //this.model = Object.assign({}, record2);

      getAction(this.url.findMax, {
        TeamworkId: iteamworkId
      }).then(function (res) {
        _this.num = res.result + 1;

        if (res.result != null) {
          _this.findMax = "不要小于" + res.result + "这个数.建议您填写:" + (res.result + 1);
        } else {
          alert(res.result);
          _this.findMax = "请输入序号";
        }

        console.log(_this.findMax);
      });
    },
    add: function add() {
      this.edit({});
    },
    edit1: function edit1(record) {
      var _this2 = this;

      console.log(record); //业务模块

      this.getBusModelSelectList(); //业务功能

      getAction(this.url.selectTaskDetail, {
        modelId: record.ibusModelId
      }).then(function (res) {
        _this2.selectList = res.result;
      }).then(function () {
        _this2.isadd = false;

        _this2.form.resetFields();

        _this2.model = Object.assign({}, record);
        _this2.visible = true;

        _this2.$nextTick(function () {
          _this2.form.setFieldsValue(pick(_this2.model, 'description', 'ibusModelId', 'ibusFunctionId', 'iorder'));

          _this2.form.setFieldsValue({
            ibusModelId: String(_this2.model.ibusModelId),
            ibusFunctionId: String(_this2.model.ibusFunctionId)
          });
        });
      });
    },
    selectTaskDetail: function selectTaskDetail() {},
    //获取选值    所属模块value   此value值时所属业务的方法的基本参数
    getModelVal: function getModelVal(e) {
      //获取对应业务列表  所需的模块儿id*/
      // this.selectList = '';
      this.busModelId = e;
      this.getFunctionList(e);
      this.form.setFieldsValue({
        ibusFunctionId: ''
      });
    },
    getBusModelSelectList: function getBusModelSelectList() {
      var _this3 = this;

      getAction(this.url.selectTaskType, {}).then(function (res) {
        _this3.modelList = res.result;
      });
    },
    // 所属业务模块数据  参数依赖于所属模块 所选id
    getFunctionList: function getFunctionList(value) {
      var _this4 = this;

      /* let url = "/papertitle/docNumSet/busFunctionList?ibusModelId="+value;*/
      value = value.toString();
      getAction(this.url.selectTaskDetail, {
        modelId: value
      }).then(function (res) {
        _this4.selectList = res.result;
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this5 = this;

      var that = this;

      if (this.$refs.indexStep.value < this.num) {
        this.$message.warning(this.findMax);
        return;
      } // 触发表单验证


      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          console.log(_this5.isadd);
          var httpurl = '';
          var method = '';

          if (_this5.isadd) {
            httpurl += _this5.url.add;
            method = 'post';
          } else {
            httpurl += _this5.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this5.model, values); //时间格式化

          /*formData.dCreateTime = formData.dCreateTime?formData.dCreateTime.format('YYYY-MM-DD HH:mm:ss'):null;
          formData.dUpdateTime = formData.dUpdateTime?formData.dUpdateTime.format('YYYY-MM-DD HH:mm:ss'):null;
          */

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message); //告诉父级组件我已关闭  关闭后刷新父级列表

              that.$emit('submit', true);
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
