  filters: {
    filterText: function filterText(text) {
      if (text.length > 18) {
        return text.substring(0, 15) + '...';
      } else {
        return text;
      }
    },
    timeStrings: function timeStrings(time) {
      var oneTime = new Date(time);
      var Y = oneTime.getFullYear();
      var M = oneTime.getMonth() + 1;
      var D = oneTime.getDate();
      return Y + '-' + (M < 10 ? "0" + M : M) + "-" + (D < 10 ? "0" + D : D) + " " + oneTime.toTimeString().substr(0, 8);
    }
  },
