import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { getAction } from '@/api/manage'; // 引入搜索部门弹出框的组件

import departWindow from "../../system/modules/DepartWindow";
import { ACCESS_TOKEN } from "@/store/mutation-types";
import { addUser, editUser, queryUserRole, queryall } from '@/api/api';
import { disabledAuthFilter } from "@/utils/authFilter";
import { duplicateCheck } from '@/api/api';
export default {
  name: "DocNumCopyModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {// ibusFunctionId:{rules: [{ required: true, message: '请输入主键id!' }]},
      },
      url: {
        add: "/papertitle/docNumSet/add",
        userWithDepart: "/papertitle/docNumSet/getDocDepartsList",
        userId: "/sys/user/generateUserId"
      },
      selectedModel: null,
      selectedFunction: null,
      modelData: [],
      functionList: [],
      functionData: [],
      upData: [],
      downData: [],
      officeData: [],
      //部门控件
      departDisabled: false,
      //是否是我的部门调用该页面
      roleDisabled: false,
      //是否是角色维护调用该页面
      modalWidth: 800,
      drawerWidth: 700,
      modaltoggleFlag: true,
      confirmDirty: false,
      selectedDepartKeys: [],
      //保存用户选择部门id
      checkedDepartKeys: [],
      checkedDepartNames: [],
      // 保存部门的名称 =>title
      checkedDepartNameString: "",
      // 保存部门的名称 =>title
      userId: "",
      //保存用户id
      disableSubmit: false,
      userDepartModel: {
        userId: '',
        departIdList: []
      } // 保存SysUserDepart的用户部门中间表数据需要的对象

    };
  },
  created: function created() {
    this.getBusModelList();
    this.getTemplateUp();
    this.getTemplateDown();
    this.getTemplateOffice();
  },
  watch: {
    selectedModel: 'getBusFunctionList'
  },
  methods: {
    add: function add(record) {
      var _this = this;

      this.form.resetFields();
      this.userId = record.iid;
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'ibusModelId', 'ibusFunctionId', 'iorder', 'sname', 'sdocRule', 'idocNum', 'iutemplateId', 'idtemplateId', 'iatemplateId', 'sremarks', 'screateBy', 'supdateBy'));
      }); // 调用查询用户对应的部门信息的方法

      this.checkedDepartKeys = [];
      this.loadCheckDeparts();
    },
    loadCheckDeparts: function loadCheckDeparts() {
      var _this2 = this;

      var that = this;

      if (!that.userId) {
        return;
      }

      getAction(that.url.userWithDepart, {
        userId: that.userId
      }).then(function (res) {
        that.checkedDepartNames = [];

        if (res.success) {
          for (var i = 0; i < res.result.length; i++) {
            that.checkedDepartNames.push(res.result[i].title);
            _this2.checkedDepartNameString = _this2.checkedDepartNames.join(",");
            that.checkedDepartKeys.push(res.result[i].key);
          }

          that.userDepartModel.departIdList = that.checkedDepartKeys;
        } else {
          console.log(res.message);
        }
      });
    },
    // 搜索用户对应的部门API
    onSearch: function onSearch() {
      this.$refs.departWindow.add(this.checkedDepartKeys, this.userId);
    },
    // 获取用户对应部门弹出框提交给返回的数据
    modalFormOk: function modalFormOk(formData) {
      this.checkedDepartNames = [];
      this.selectedDepartKeys = [];
      this.checkedDepartNameString = '';
      this.userId = formData.userId;
      this.userDepartModel.userId = formData.userId;

      for (var i = 0; i < formData.departIdList.length; i++) {
        this.selectedDepartKeys.push(formData.departIdList[i].key);
        this.checkedDepartNames.push(formData.departIdList[i].title);
        this.checkedDepartNameString = this.checkedDepartNames.join(",");
      }

      this.userDepartModel.departIdList = this.selectedDepartKeys;
      this.checkedDepartKeys = this.selectedDepartKeys; //更新当前的选择keys

      console.log("=====>" + this.checkedDepartKeys);
    },
    refresh: function refresh() {
      this.selectedDepartKeys = [];
      this.checkedDepartKeys = [];
      this.checkedDepartNames = [];
      this.checkedDepartNameString = "";
      this.userId = "";
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.disableSubmit = false;
      this.userDepartModel = {
        userId: '',
        departIdList: []
      };
      this.checkedDepartNames = [];
      this.checkedDepartNameString = '';
      this.checkedDepartKeys = [];
      this.selectedDepartKeys = [];
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
