    return {
      visible: false,
      uploading: false,
      fileList: [],
      uploadAction: ''
    };
  },
  watch: {
    url: function url(val) {
      if (val) {
        this.uploadAction = window._CONFIG['domianURL'] + val;
      }
    }
  },
