import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "OaButtonSetModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      data: [],
      selectButton: '',
      buttonInfo: '',
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oabuttonset/oaButtonSet/add",
        edit: "/oabuttonset/oaButtonSet/edit",
        buttonList: "/oabutton/oaButton/buttonList",
        queryById: "/oabutton/oaButton/queryById"
      }
    };
  },
  created: function created() {},
  methods: {
    //add () {
    //this.edit({});
    //},
    getButtonList: function getButtonList() {
      var _this = this;

      getAction(this.url.buttonList, {}).then(function (res) {
        _this.data = res.result;
      });
    },
    getButtonId: function getButtonId() {
      this.model.ibuttonId = this.selectButton;
    },
    add: function add(record) {
      var _this2 = this;

      this.form.resetFields();
      this.model = Object.assign({}, record); //---------------根据按钮ID 请求名字--------------

      getAction(this.url.queryById, {
        id: this.model.ibuttonId
      }).then(function (res) {
        _this2.buttonInfo = res.result;
        _this2.selectButton = _this2.buttonInfo.sbtnName;
      }); //------------------------------

      this.visible = true;
      this.$nextTick(function () {
        _this2.form.setFieldsValue(pick(_this2.model, 'iid', 'procdefKey', 'taskdefKey', 'iprocButtonId', 'ibuttonId', 'iorder', 'ipermitType', 'iisCreater', 'iisReader', 'iisLastsender', 'iisTransactors', 'iisDefault', 'itaskDefKey', 'smanagerRoleId')); //时间格式化

      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      this.close();
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
