import { httpAction, getAction, postAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "oaTeamworkSetModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      modelList: [],
      selectList: [],
      modelId: '',
      functionId: '',
      model: {
        iteamworkId: '',
        steamworkName: '',
        iversion: ''
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oateamwork/oaTeamworkSet/add",
        edit: "/oateamwork/oaTeamworkSet/edit"
      },
      //用来判断弹框是用来新增或是编辑两种状态
      isadd: false
    };
  },
  created: function created() {
    this.getBusModelSelectList();
  },
  methods: {
    add1: function add1(iteamworkId, steamworkName, iversion) {
      this.isadd = true;
      this.model.iteamworkId = iteamworkId;
      this.model.iversion = Number(iversion);
      this.visible = true;
      this.title = steamworkName;
      this.form.resetFields(); //this.model = Object.assign({}, record2);
    },
    add: function add() {
      this.edit({});
    },
    edit1: function edit1(record) {
      var _this = this;

      this.isadd = false;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'iteamworkId', 'iorder', 'description', 'ibusModelId', 'ibusFunctionId', 'screateBy', 'screateDeptid', 'screateUnitid', 'iversion')); //时间格式化

        /*this.form.setFieldsValue({dCreateTime:this.model.dCreateTime?moment(this.model.dCreateTime):null})
         this.form.setFieldsValue({dUpdateTime:this.model.dUpdateTime?moment(this.model.dUpdateTime):null})*/

      });
    },
    //获取选值    所属模块value   此value值时所属业务的方法的基本参数
    getModelVal: function getModelVal(e) {
      //获取对应业务列表  所需的模块儿id*/
      this.getFunctionList(e);
    },
    getBusModelSelectList: function getBusModelSelectList() {
      var _this2 = this;

      var url = "/oaBus/busModel/findList";
      getAction(url, {}).then(function (res) {
        _this2.modelList = res.result;
      });
    },
    // 所属业务模块数据  参数依赖于所属模块 所选id
    getFunctionList: function getFunctionList(value) {
      var _this3 = this;

      var url = "/papertitle/docNumSet/busFunctionList?ibusModelId=" + value;
      getAction(url).then(function (res) {
        _this3.selectList = res.result;
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this4 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          console.log(_this4.isadd);
          var httpurl = '';
          var method = '';

          if (_this4.isadd) {
            httpurl += _this4.url.add;
            method = 'post';
          } else {
            httpurl += _this4.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this4.model, values); //时间格式化

          /*formData.dCreateTime = formData.dCreateTime?formData.dCreateTime.format('YYYY-MM-DD HH:mm:ss'):null;
          formData.dUpdateTime = formData.dUpdateTime?formData.dUpdateTime.format('YYYY-MM-DD HH:mm:ss'):null;
          */

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message); //告诉父级组件我已关闭  关闭后刷新父级列表

              that.$emit('submit', true);
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
