    //获取结束时间
    getEndTime: function getEndTime(a) {
      console.log(a._d);
      this.model.dEndTime = a._d;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this3 = this;

      // console.log(this.form.getFieldsValue('stitle'));
      // console.log(this.ischeck);
      // console.log(this.userRealName);
      // if(this.model.stitle==undefined){
      //   this.$message.warning("标题不能为空");
      //   return ;
      // }
      if (!this.ischeck) {
        var that = this; // 触发表单验证

        this.form.validateFields(function (err, values) {
          if (!err) {
            var formData = Object.assign(_this3.model, values);
            that.confirmLoading = true;
            var httpurl = '';
            var method = '';

            if (!_this3.model.iid) {
              httpurl += _this3.url.add;
              method = 'post';
              formData.dStartTime = _this3.timer(_this3.model.dStartTime);
              formData.dstartTime = _this3.timer(_this3.model.dStartTime);
              formData.dEndTime = _this3.timer(_this3.model.dEndTime);
              formData.dendTime = _this3.timer(_this3.model.dEndTime);
            } else {
              httpurl += _this3.url.edit;
              method = 'put';
              formData.dStartTime = _this3.timer(_this3.model.dStartTime);
              formData.dstartTime = _this3.timer(_this3.model.dStartTime);
              formData.dEndTime = _this3.timer(_this3.model.dEndTime);
              formData.dendTime = _this3.timer(_this3.model.dEndTime);
            }

            formData.suserNames = _this3.model.suserNames;
            var ds = Object.keys(formData);
            putAction(httpurl, formData, method).then(function (res) {
              if (res.success) {
                that.$message.success(res.message); // that.$emit('myrichenClose',true);

                that.reload();
              } else {
                that.$message.warning(res.message);
              }
            }).finally(function () {
              that.confirmLoading = false;
              that.close();
            });
          }
        });
      } else {
        this.$message.error('您现在为查看状态，不能更改'); // this.close()
      }
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    timer: function timer(timeval) {
      var date1 = new Date(timeval);
      var Y = date1.getFullYear();
      var M = date1.getMonth() + 1;
      var D = date1.getDate();
      var newTime = Y + '-' + (M < 10 ? "0" + M : M) + "-" + (D < 10 ? "0" + D : D) + " " + date1.toTimeString().substr(0, 8);
      console.log(newTime);
      return newTime;
    }
  }
};
