import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "oaBusdataModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oaBus/oaBusdata/add",
        edit: "/oaBus/oaBusdata/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iId', 'iBusModelId', 'iBusFunctionId', 'procInstId', 'sMainUnitNames', 'sCcUnitNames', 'sInsideDeptnames', 'sReportNuitNames', 'sCrcDeptnames', 'sTitle', 'sLeftParameter', 'sUnitName', 'sDeptName', 'sMiddleParameter', 'sRightParameter', 'sReceiveNum', 'sFileNum', 'iIsRelease', 'iIsState', 'iIsSealdate', 'iIsTypeset', 'iIsApprove', 'iIsArchives', 'iIsEs', 'iIsSend', 'iIsDisplay', 'sRemarks', 'iCreateYear', 'iCreateMonth', 'iCreateDay', 'sCreateName', 'sCreateBy', 'sCreateDept', 'iBigint1', 'iBigint2', 'iBigint3', 'iBigint4', 'sVarchar1', 'sVarchar2', 'sVarchar3', 'sVarchar4', 'sVarchar5', 'sVarchar6', 'sVarchar7', 'sVarchar8', 'sVarchar9', 'sVarchar10', 'sVarchar11', 'sVarchar12', 'sVarchar13', 'sVarchar14', 'sVarchar15', 'sVarchar16', 'sVarchar17', 'sVarchar18', 'sVarchar19', 'sVarchar20', 'iIs1', 'iIs2', 'iIs3', 'iIs4', 'iIs5')); //时间格式化


        _this.form.setFieldsValue({
          dCreateTime: _this.model.dCreateTime ? moment(_this.model.dCreateTime) : null
        });

        _this.form.setFieldsValue({
          dUpdateTime: _this.model.dUpdateTime ? moment(_this.model.dUpdateTime) : null
        });

        _this.form.setFieldsValue({
          dDatetime1: _this.model.dDatetime1 ? moment(_this.model.dDatetime1) : null
        });

        _this.form.setFieldsValue({
          dDatetime2: _this.model.dDatetime2 ? moment(_this.model.dDatetime2) : null
        });

        _this.form.setFieldsValue({
          dDatetime3: _this.model.dDatetime3 ? moment(_this.model.dDatetime3) : null
        });

        _this.form.setFieldsValue({
          dDatetime4: _this.model.dDatetime4 ? moment(_this.model.dDatetime4) : null
        });

        _this.form.setFieldsValue({
          dDatetime5: _this.model.dDatetime5 ? moment(_this.model.dDatetime5) : null
        });

        _this.form.setFieldsValue({
          dDatetime6: _this.model.dDatetime6 ? moment(_this.model.dDatetime6) : null
        });
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values); //时间格式化

          formData.dCreateTime = formData.dCreateTime ? formData.dCreateTime.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dUpdateTime = formData.dUpdateTime ? formData.dUpdateTime.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime1 = formData.dDatetime1 ? formData.dDatetime1.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime2 = formData.dDatetime2 ? formData.dDatetime2.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime3 = formData.dDatetime3 ? formData.dDatetime3.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime4 = formData.dDatetime4 ? formData.dDatetime4.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime5 = formData.dDatetime5 ? formData.dDatetime5.format('YYYY-MM-DD HH:mm:ss') : null;
          formData.dDatetime6 = formData.dDatetime6 ? formData.dDatetime6.format('YYYY-MM-DD HH:mm:ss') : null;
          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
