import { httpAction, getAction } from '@/api/manage';
import pick from 'lodash.pick';
import moment from "moment";
import AFormItem from "ant-design-vue/es/form/FormItem";
export default {
  name: "OaButtonSetModal",
  components: {
    AFormItem: AFormItem
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      data: [],
      buttonInfo: '',
      selectButton: '',
      iprocButtonId: '',
      ipermitType: 0,
      iisCreater: 0,
      iisReader: 0,
      // sbtnName:'',
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {
          rules: [{
            required: true,
            message: '请输入主键id!'
          }]
        }
      },
      url: {
        add: "/oabuttonset/oaButtonSet/add",
        edit: "/oabuttonset/oaButtonSet/edit",
        buttonList: "/oabutton/oaButton/buttonList",
        queryById: "/oabutton/oaButton/queryById"
      }
    };
  },
  created: function created() {
    this.getButtonList();
  },
  methods: {
    add: function add(res) {
      this.iprocButtonId = res;
      this.ipermitType = 0;
      this.iisCreater = 0;
      this.iisReader = 0; // console.log("6666666666666666666666666666666666666");
      // console.log(res);
      // console.log(this.model);

      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record); //---------------根据按钮ID 请求名字--------------

      getAction(this.url.queryById, {
        id: this.model.ibuttonId
      }).then(function (res) {
        _this.buttonInfo = res.result;
        _this.selectButton = _this.buttonInfo.sbtnName;
      });
      this.ipermitType = this.model.ipermitType;
      this.iisCreater = this.model.iisCreater;
      this.iisReader = this.model.iisReader; // iisCreater:0,
      // iisReader:0,
      // this.selectButton=this.model.sbtnName;
      //------------------------------

      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'iid', 'procdefKey', 'taskdefKey', 'iprocButtonId', 'ibuttonId', 'iorder', 'ipermitType', 'iisCreater', 'iisReader', 'iisLastsender', 'iisTransactors', 'iisDefault', 'itaskDefKey', 'smanagerRoleId')); //时间格式化

      });
    },
    getButtonList: function getButtonList() {
      var _this2 = this;

      getAction(this.url.buttonList, {}).then(function (res) {
        _this2.data = res.result;
      });
    },
    getButtonId: function getButtonId() {
      this.model.ibuttonId = this.selectButton;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this3 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this3.model.iid) {
            httpurl += _this3.url.add;
            console.log("0000000000000000000000000");
            _this3.model.iprocButtonId = _this3.iprocButtonId;
            method = 'post';
          } else {
            httpurl += _this3.url.edit;
            method = 'put';
          }

          _this3.model.ipermitType = _this3.ipermitType;
          _this3.model.iisCreater = _this3.iisCreater;
          _this3.model.iisReader = _this3.iisReader;
          console.log(_this3.model);
          var formData = Object.assign(_this3.model, values); //时间格式化

          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
