        this.picDetail.upFileName2 = res.result.sfileName;
        this.picDetail.fileId2 = res.result.iid;
        this.show2(res.result.iid);
        this.$message.success("上传logo成功");
        this.$emit('ok');
      } else {
        this.$message.error("上传logo失败");
      }
    })
    return true;
  },

    //确认替换
