      if (this.picDetail.fileId != null){
        getAction(this.url.updatePic,{id:this.picDetail.fileId}).then(res => {
          // if (res.success){
          //   this.$message.success(res.message)
          // } else {
          //   this.$message.error(res.message);
          // }
        })
      }
