import { httpAction } from '@/api/manage'
import pick from 'lodash.pick'

export default {
  name: "OaProcButtonCopyModal",
  data () {
    return {
      title:"操作",
      visible: false,
      model: {},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },

      confirmLoading: false,
      form: this.$form.createForm(this),
      // validatorRules:{
      //   iId:{rules: [{ required: true, message: '请输入主键id!' }]},
      // },
      url: {
        // add: "/oaprocbutton/oaProcButton/add",
        // edit: "/oaprocbutton/oaProcButton/edit",
        copy: "/oaprocbutton/oaProcButton/copeConfig"
      },
    }
  },
  created () {
  },
  methods: {
    // add () {
    //   this.edit({});
    // },
    // edit
      copy(record) {
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.model,'iid','sbuttonSetName','procDefKey'))
        //时间格式化
      });

    },
    close () {
      this.$emit('close');
      this.visible = false;
    },
    handleOk () {
      const that = this;
      // 触发表单验证
      this.form.validateFields((err, values) => {
        if (!err) {
          that.confirmLoading = true;
          let httpurl = '';
          let method = '';
          // if(!this.model.iid){
          //   httpurl+=this.url.add;
          //   method = 'post';
          // }else{
            httpurl+=this.url.copy;
            method = 'post';
          // }
          let formData = Object.assign(this.model, values);
          //时间格式化

          console.log(formData)
          httpAction(httpurl,formData,method).then((res)=>{
            if(res.success){
              that.$message.success(res.message);
              that.$emit('ok');
            }else{
              that.$message.warning(res.message);
            }
          }).finally(() => {
            that.confirmLoading = false;
            that.close();
          })



        }
      })
    },
    handleCancel () {
      this.close()
    },


  }
}
