import {httpAction, getAction} from '@/api/manage'
import pick from 'lodash.pick'
import moment from "moment"

export default {
  name: "BusFunctionViewModal",
  data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      typeModel: '',
      roleId: '',
      roleList: [],
      labelCol: {
        xs: {span: 24},
        sm: {span: 5},
      },
      wrapperCol: {
        xs: {span: 24},
        sm: {span: 16},
      },

      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        iId: {rules: [{required: true, message: '请输入主键id!'}]},
      },
      url: {
        add: "/oaBus/busFunctionView/add",
        edit: "/oaBus/busFunctionView/edit",
      },
    }
  },
  created() {
    this.initialRoleList();
  },
  methods: {
    initialRoleList: function () {
      let url = "/sys/role/queryall";
      getAction(url).then((res) => {
        //console.log(res.result);
        this.roleList = res.result;
      })
    },
    add() {
      this.visible = true;
    },
    close() {
      /*document.getElementById("#stypeModel").empty();*/
      this.typeModel = '';
      this.roleId = '';
      this.$emit('close');
      this.visible = false;
    },
    handleOk() {
      let flag = true;
      if (this.typeModel == '' || this.typeModel == null) {
        flag = false;
      }
      if (this.roleId == '' || this.roleId == null) {
        flag = false;
      }
      if (flag) {
        let roleObj = {"itype": this.typeModel, "iroleId": this.roleId};
        this.$emit("ok", roleObj);
        this.close()
      }


    },
    handleCancel() {
      this.close()
    },


  }
}
