  methods: {
    yinru(record) {
      // alert(111);
      // this.getFileNameById(record.ifileId);
      this.form.resetFields();
      // this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.model,'iid','itype','sname','ifileId','ititleRuleId','sremarks'))
      });
    },
    close () {
      this.$emit('close');
      this.visible = false;
      this.upFileName = '';
    },
    use(e){
      console.log(e);
    },
    handleOk () {
      this.close()
    },
    handleDenji () {
      this.close()
    },
    getFileNameById(id){
      let url = "/papertitle/oaTemplate/getFileNameById";
      getAction(url,{ifileId:id}).then((res)=>{
        this.upFileName = res.result.fileName;
      })
    },
    getSelection(){
      let url = "/papertitle/paperTitleSetting/paperTitleList";
      getAction(url).then((res)=>{
        this.selectData = res.result;
      })
    },
    downFileName(fileName){
      if(!fileName || typeof fileName != "string"){
        fileName = "导出文件"
      }
      let param = {...this.queryParam};
      if(this.selectedRowKeys && this.selectedRowKeys.length>0){
        param['selections'] = this.selectedRowKeys.join(",")
      }
      console.log("导出参数",param)
      downFile(this.url.exportXlsUrl,param).then((data)=>{
        if (!data) {
          this.$message.warning("文件下载失败")
          return
        }
        if (typeof window.navigator.msSaveBlob !== 'undefined') {
          window.navigator.msSaveBlob(new Blob([data]), fileName+'.xls')
        }else{
          let url = window.URL.createObjectURL(new Blob([data]))
          let link = document.createElement('a')
          link.style.display = 'none'
          link.href = url
          link.setAttribute('download', fileName+'.xls')
          document.body.appendChild(link)
          link.click()
          document.body.removeChild(link); //下载完成移除元素
          window.URL.revokeObjectURL(url); //释放掉blob对象
        }
      })
    }

  }
}
