    getCaption(obj,state){
      let index = obj.lastIndexOf("\-");
      if(state == 0){
        obj = obj.substring(0,index);
      }else{
        obj = obj.substring(index+1,obj.length)
      }
      return obj;
    },
    getPgSecondList(taskNames) {
      this.taskNames = taskNames[taskNames.length-1];
