                dataIndex: this.searchColumns[i].s_table_column,
                align: "left",
                sorter:(a,b,type)=>{
                  if(type == 'descend'){
                    this.queryParam.orderFlag = 's_title desc'
                  }else{
                    this.queryParam.orderFlag = 's_title asc'
                  }
                  //a.s_title.length - b.s_title.length ;
                },
              });
            }
            else{
              this.columns.push({
                title: this.searchColumns[i].s_column_name,
                dataIndex: this.searchColumns[i].s_table_column,
                align: "center",
                sorter:(a,b,type)=>{
