result:
.drag{
  position: relative;
  background-color: #e8e8e8;
  width: 100%;
  height: 34px;
  line-height: 34px;
  text-align: center;
}
.handler{
  width: 40px;
  height: 32px;
  border: 1px solid #ccc;
  cursor: move;
}
.handler_bg{
  background: #fff url("data:image/png;base64,") no-repeat center;
}
.handler_ok_bg{
  background: #fff url("data:image/png;base64,") no-repeat center;
}
.drag_bg{
  background-color: #7ac23c;
  height: 34px;
  width: 0px;
}
.drag_text{
  position: absolute;
  top: 0px;
  width: 100%;text-align: center;
  -moz-user-select: none;
  -webkit-user-select: none;
  user-select: none;
  -o-user-select:none;
  -ms-user-select:none;
}
