  import { httpAction,getAction,postAction } from '@/api/manage'
  import pick from 'lodash.pick'
  import moment from "moment"

  export default {
    name: "oaTeamworkSetModal",
    data () {
      return {
        title:"操作",
        visible: false,
        modelList: [],
        selectList: [],
        modelId:'',
        functionId:'',
        model: {
          iteamworkId:'',
          steamworkName:'',
          iversion:'',
        },
        labelCol: {
          xs: { span: 24 },
          sm: { span: 5 },
        },
        wrapperCol: {
          xs: { span: 24 },
          sm: { span: 16 },
        },

        confirmLoading: false,
        form: this.$form.createForm(this),
        validatorRules:{
        iId:{rules: [{ required: true, message: '请输入主键id!' }]},
        },
        url: {
          add: "/oateamwork/oaTeamworkSet/add",
          edit: "/oateamwork/oaTeamworkSet/edit",
        },

        //用来判断弹框是用来新增或是编辑两种状态
        isadd:false
      }
    },
    created() {
      this.getBusModelSelectList();
    },
    methods: {
      add1(iteamworkId,steamworkName,iversion) {
        this.isadd = true;
        this.model.iteamworkId = iteamworkId;
        this.model.iversion = Number(iversion);
        this.visible = true
        this.title = steamworkName;
        this.form.resetFields();
        //this.model = Object.assign({}, record2);
      },
      add () {
        this.edit({});
      },
      edit1 (record) {
        this.isadd = false;
        this.form.resetFields();
        this.model = Object.assign({}, record);
        this.visible = true;
        this.$nextTick(() => {
          this.form.setFieldsValue(pick(this.model,'iid','iteamworkId','iorder','description','ibusModelId','ibusFunctionId','screateBy','screateDeptid','screateUnitid','iversion'))
		  //时间格式化
         /*this.form.setFieldsValue({dCreateTime:this.model.dCreateTime?moment(this.model.dCreateTime):null})
          this.form.setFieldsValue({dUpdateTime:this.model.dUpdateTime?moment(this.model.dUpdateTime):null})*/
        });

      },
      //获取选值    所属模块value   此value值时所属业务的方法的基本参数
      getModelVal(e){
        //获取对应业务列表  所需的模块儿id*/
        this.getFunctionList(e);

      },
      getBusModelSelectList(){
        let url = "/oaBus/busModel/findList";
        getAction(url, {}).then((res) => {
          this.modelList=res.result;
        });
      },
      // 所属业务模块数据  参数依赖于所属模块 所选id
      getFunctionList(value){
        let url = "/papertitle/docNumSet/busFunctionList?ibusModelId="+value;
        getAction(url).then((res)=>{
          this.selectList = res.result;
        })

      },
      close () {
        this.$emit('close');
        this.visible = false;
      },
      handleOk () {
        const that = this;
        // 触发表单验证
        this.form.validateFields((err, values) => {
          if (!err) {
            that.confirmLoading = true;
            console.log(this.isadd)
            let httpurl = '';
            let method = '';
            if (this.isadd) {
              httpurl+=this.url.add;
              method = 'post';
            }else{
              httpurl+=this.url.edit;
               method = 'put';
            }
            let formData = Object.assign(this.model, values);
            //时间格式化
            /*formData.dCreateTime = formData.dCreateTime?formData.dCreateTime.format('YYYY-MM-DD HH:mm:ss'):null;
            formData.dUpdateTime = formData.dUpdateTime?formData.dUpdateTime.format('YYYY-MM-DD HH:mm:ss'):null;
            */
            console.log(formData)
            httpAction(httpurl,formData,method).then((res)=>{
              if(res.success){
                that.$message.success(res.message);
                //告诉父级组件我已关闭  关闭后刷新父级列表
                that.$emit('submit',true);
              }else{
                that.$message.warning(res.message);
              }
            }).finally(() => {
              that.confirmLoading = false;
              that.close();
            })



          }
        })
      },
      handleCancel () {
        this.close()
      },


    }
  }
