import JSelectMultiUserModal from './modal/JSelectMultiUserModal'
export default {
  name: 'JSelectMultiUser',
  components:{ JSelectMultiUserModal },
  props:{
    value:{
      type:String,
      required:false
    }
  },
  data(){
    return {
      selectList: [],
    }
  },
  computed: {
    nameList: function () {
      var names = [];
      for (var a = 0; a < this.selectList.length; a++) {
        names.push(this.selectList[a].name);
      }
      let nameStr = ''
      if(names.length>0){
        nameStr = names.join(",")
      }
      this.$emit("change",nameStr)
      return names;
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  },
  methods:{
    handleSelect: function () {
      this.$refs.selectModal.add();
    },
    selectReset() {
      this.selectList = [];
    },
    selectOK: function (data) {
      this.selectList = data;
    }
  }
}
