import { httpAction } from '@/api/manage'
import { getAction,postAction} from '@/api/manage'
import { JeecgListMixin } from '@/mixins/JeecgListMixin'

import Vue from 'vue'

export default {
  name: "BusFunctionCatModal",
  mixins:[JeecgListMixin],
  data () {
    return {
      isshowType:'1',
      typeModel:'',//范围类型    需要判断  分别值有 0 1 2 3
      checkedDepartNameString:'',
      confirmLoading:false,
      selectvalue:'1',  //是否选中
      itypeId:'',
      itypeName:'',
      title: "操作",
      visible: false,
      selectedRole:'',//
      selectedUser:'',//
      labelCol: {
        xs: {span: 24},
        sm: {span: 5},
      },
      wrapperCol: {
        xs: {span: 24},
        sm: {span: 16},
      },
    }
  },
  created(){
    this.getSelection();
  },
  mounted(){

  },
  methods: {
    showCat(record) {
      console.log(record);
      this.typeModel = record.spermitType;
      this.$nextTick(() => {
        if(record.spermitType=='1'){
          this.isshowType = 1;
          this.selectedRole = record.itypeName;
          this.typeModel = '角色';
        }else if(record.spermitType=='3'){
          this.isshowType = 3;
          this.selectedUser = record.itypeName;
          this.typeModel = '人员';
        }else if(record.spermitType=='2'){
          this.isshowType = 2;
          this.typeModel = '部门';
          this.checkedDepartNameString =record. itypeName
        }else{
          this.isshowType = 0;
          this.typeModel = '所有人'
        }

        this.selectvalue = Number(record.sdisplay);
        this.visible = true;
      });



    },
    close () {
      this.$emit('close');
      this.visible = false;
    },
    handleOk () {
      this.close()
    },
    handleCancel () {
      this.close()
    },


  }
}
