      this.routeSwitch = e;
    },
    selectval(e) {
      if (this.routeSwitch) {
        this.areatext += this.suggesttionLists[e].scontent + ' ';
      } else {
        this.areatext = '';
        this.areatext = this.suggesttionLists[e].scontent;
      }
    },
    toModal: function () {
      this.$refs.userOpinionForm.show();
